/// <summary>
/// This is parm class, used to provide a common information about a Print management node,
/// as needed for different Docentric Print management utilities.
/// </summary>
public class DocPrintMgmtNodeInfo
{
    private boolean isValid;
    private Common referencedRecord;
    private str menuItemName;
    private str nodeSpecificId;
    private PrintMgmtReportFormatCountryRegionId countryRegionId;

}

Method.Source:
/// <summary>
/// Gets/Sets the flag indicating whether the print management node info is valid.
/// </summary>
/// <param name = "_isValid">Is print management node info valid</param>
/// <returns>True if the print management node info is valid; otherwise false</returns>
public boolean parmIsValid(boolean _isValid = isValid)
{
    isValid = _isValid;
    return isValid;
}

/// <summary>
/// Gets/Sets the referenced record for the current print management node.
/// </summary>
/// <param name = "_referencedRecord">Referenced record</param>
/// <returns>Referenced record for tuhe current print management node</returns>
public Common parmReferencedRecord(Common _referencedRecord = referencedRecord)
{
    referencedRecord = _referencedRecord;
    return referencedRecord;
}

/// <summary>
/// Gets/Sets the name of the referenced menu item for the current print management node.
/// </summary>
/// <param name = "_menuItemName">Name of the referenced menu item</param>
/// <returns>Name of the referenced menu item for the current print management node</returns>
public str parmMenuItemName(str _menuItemName = menuItemName)
{
    menuItemName = _menuItemName;
    return menuItemName;
}

/// <summary>
/// Gets/Sets the Id specific to the current print management node (e.g. VendTable.AccountNum).
/// </summary>
/// <param name = "_nodeSpecificId">Id specific to the current print management node</param>
/// <returns>Id specific to the current print management node</returns>
public str parmNodeSpecificId(str _nodeSpecificId = nodeSpecificId)
{
    nodeSpecificId = _nodeSpecificId;
    return nodeSpecificId;
}

/// <summary>
/// Gets/Sets the country region Id for the current print management node.
/// </summary>
/// <param name = "_countryRegionId">Country region Id</param>
/// <returns>Country region Id</returns>
public PrintMgmtReportFormatCountryRegionId parmCountryRegionId(PrintMgmtReportFormatCountryRegionId _countryRegionId = countryRegionId)
{
    countryRegionId = _countryRegionId;
    return countryRegionId;
}
