Method.Source:
/// <summary>
/// Display method for the archived report filename in PDF format.
/// </summary>
/// <returns>PDF report filename</returns>
[SysClientCacheDataMethodAttribute(true)]
public display Filename pdfReportFilename_DC()
{
    return DocPrintArchiveHelper::getReportDocuRef_InPDF(this).docuValue().OriginalFileName;
}

/// <summary>
/// Display method for the archived report filename in non-PDF format.
/// </summary>
/// <returns>Non-PDF report filename</returns>
[SysClientCacheDataMethodAttribute(true)]
public display Filename nonPdfReportFilename_DC()
{
    DocPrintJobHeader docPrintJobHeader = DocPrintJobHeader::findByPrintJobHeader(this.RecId);

    return DocPrintArchiveHelper::getReportDocuRef_InNonPDF(this, docPrintJobHeader).docuValue().OriginalFileName;
}
