public class DocLabelTranslationByLabel extends FormRun implements IDocRefreshable
{
    boolean shouldPositionToCallerTranslation = false;
    boolean shouldPositionToCallerLabel = false;
    DocLabelTranslation callerTranslation;
    DocReportLabelId callerLabelId;
    RecId firstAddedTranslationRecId;
    str selectedFilterLanguageId;
    QueryBuildDataSource qbdsTranslationFilter;

}

/// <summary>
/// Initializes the form.
/// </summary>
public void init()
{
    super();

    element.checkCaller();

    // Init the controls
    element.setControls();
}

/// <summary>
/// Checks the args passed by the caller.
/// </summary>
private void checkCaller()
{
    if (element.args() && element.args().record())
    {
        QueryBuildDataSource qbdsGlobalLabels = DocLabelTable_ds.query().dataSourceTable(tableNum(DocLabelTable));

        if (element.args().record() is DocReportLabel)
        {
            // If we came from the Docentric report setup form, and we were positioned on one label,
            // then select that label.
            if (element.args().caller() is FormRun)
            {
                FormRun callerForm = element.args().caller();
                FormDataSource callerFds;
                for (int i = 1; i <= callerForm.dataSourceCount(); i++)
                {
                    if (callerForm.dataSource(i).cursor() is DocReportLabel)
                    {
                        callerFds = callerForm.dataSource(i);
                        if (callerFds.recordsMarked().lastIndex() <= 1)
                        {
                            // Only 1 selected caller label.
                            // If it is user-defined label, get positioned to it.
                            DocReportLabel callerDocReportLabel = element.args().record() as DocReportLabel;
                            if (DocUserDefinedLabelHelper::isUserDefinedLabelId(callerDocReportLabel.LabelId))
                            {
                                callerLabelId = callerDocReportLabel.LabelId;
                                shouldPositionToCallerLabel = true;
                            }
                        }
                    }
                }
            }
        }
        else if (element.args().record() is DocLabelLanguage)
        {
            // If we came from the label language or translation by language form 
            // and we were positioned on one language, then show translations in that language
            DocLabelLanguage callerLanguage = element.args().record() as DocLabelLanguage;
            ShowTranslationInLanguage.text(callerLanguage.LanguageId);
            ShowTranslationInLanguage.modified();
        }
        else if (element.args().record() is DocLabelGroup)
        {
            // If we came from the label group form and we were positioned on one group, 
            // then filter by that group
            DocLabelGroup callerGroup = element.args().record() as DocLabelGroup;
            qbdsGlobalLabels.addRange(fieldNum(DocLabelTable, GroupId)).value(queryValue(callerGroup.GroupId));
        }
        else if (element.args().record() is DocLabelTranslation)
        {
            // If we came through jumpRef from some form which passed the translation record 
            // as a caller record, and the main datasource of the caller form matches
            // some of this form datasources, that datasource will be filtered in this form.
            // Examples:
            //      1. We came from translations by language, where language is a main datasource.
            //         --> On this form language datasource will be filtered by the caller form's
            //         language.
            //      2. We came from all translations form, where translations are a main datasource.
            // Additionally, we want to position the cursor in the translations dataset 
            // to the same translation record as in the caller form (i.e. same label and same language). 
            // We will do that during the first execution of the linkActive() method 
            // of the translation datasource.
            callerTranslation = element.args().record() as DocLabelTranslation;
            shouldPositionToCallerTranslation = true;
        }
    }
}

/// <summary>
/// Sets the controls on the form.
/// </summary>
public void setControls()
{
    boolean isGridView = GridPanel.isActivePage();

    // Show menu items related to label bulk operations only in grid view.
    TranslationsButtonGroup.visible(isGridView);

    // Show/hide the language filter combo. If no translation filter is active,
    // don't show the language filter combo.
    LanguageId.visible(LabelFilterCombo.selection() != DocLabelTranslationFilter::All);

    // Set the translation language as the first (top-priority) label language
    // if it wasn't set previously (by coming from one of the language-related
    // forms)
    if (ShowTranslationInLanguage.text() == '')
    {
        ShowTranslationInLanguage.text(DocLabelLanguage::getFirstLanguageId());
        ShowTranslationInLanguage.modified();
    }

    // Force translations query
    DocLabelTranslation_ds.executeQuery();
}

/// <summary>
/// Called when the form is closing.
/// </summary>
public void close()
{
    FormDataSource callerFormDs;

    // If we came from the Docentric report setup form, refresh the DocReportLabel datasource.
    if (element.args() && element.args().caller() && (element.args().caller() is FormRun))
    {
        FormRun callerFormRun = element.args().caller() as FormRun;
        if (callerFormRun.name() == formStr(DocReportTable))
        {
            for (int i = 1; i <= callerFormRun.dataSourceCount(); i++)
            {
                if (callerFormRun.dataSource(i).cursor() is DocReportLabel)
                {
                    callerFormDs = callerFormRun.dataSource(i);
                    callerFormDs.research(true);
                    break;
                }
            }
        }
    }

    super();
}

