/// <summary>
/// Runs the form.
/// </summary>
public void run()
{
    super();

    #SysSystemDefinedButtons

    // Show always List when opened in the Details Form View Option.
    FormViewOption formViewOption = this.viewOptionHelper().getViewOption();
    if (formViewOption == FormViewOption::Details)
    {
        FormCommandButtonControl buttonShowList = this.control(this.controlId(#SystemDefinedShowListButton)) as FormCommandButtonControl;
        FormControl navigationListControl = this.control(this.controlId('NavigationList'));
        if (buttonShowList && navigationListControl)
        {
            buttonShowList.clicked();
            navigationListControl.visible(true);
        }
    }
}

