/// <summary>
/// Executes the query.
/// </summary>
public void executeQuery()
{
    QueryBuildDataSource qbds = this.query().dataSourceTable(tableNum(DocLabelTable));

    qbds.clearRanges();

    // If we are in a grid view, 
    // and we selected to filter only the translated or not translated labels in specified language,
    // and language is provided
    if (GridPanel.isActivePage() && (LabelFilterCombo.selection() != DocLabelTranslationFilter::All) && (LanguageId.text() != ''))
    {
        qbdsTranslationFilter.clearRanges();

        // We will use exist/not exist join with translations different from
        // empty string for the selected language                
        str filterString = strFmt('((%1.Translation != "") && (%1.LanguageId == "%2"))',
                                        qbdsTranslationFilter.name(), LanguageId.text());
        qbdsTranslationFilter.addRange(fieldNum(DocLabelTranslation, RecId)).value(filterString);

        // Temporarily enable the helper translations datasource,
        // until super() executes the query, then we will disable it.
        qbdsTranslationFilter.enabled(true);

        switch (LabelFilterCombo.selection())
        {
            case DocLabelTranslationFilter::Translated:
                qbdsTranslationFilter.joinMode(JoinMode::ExistsJoin);
                break;

            case DocLabelTranslationFilter::NotTranslated:
                qbdsTranslationFilter.joinMode(JoinMode::NoExistsJoin);
                break;

            default:
            // do nothing
        }
    }

    // Execute the query (i.e. run super()) and then disable the helper translations
    // datasource until the next time we need it.
    super();

    qbdsTranslationFilter.enabled(false);
}

/// <summary>
/// Initializes the datasource.
/// </summary>
public void init()
{
    super();

    QueryBuildDataSource qbds = this.query().dataSourceTable(tableNum(DocLabelTable));

    // Link a helper translations datasource, but keep it disabled.
    // We will enable it only temporarily in executeQuery() method,
    // when we need to filter translated or not translated labels.
    qbdsTranslationFilter = qbds.addDataSource(tableNum(DocLabelTranslation));
    qbdsTranslationFilter.relations(true);
    qbdsTranslationFilter.enabled(false);
}

/// <summary>
/// Display method that returns the translation of the current label
/// in the language selected on the form. Fallback translations are
/// taken into account where applicable.
/// </summary>
/// <param name = "_label">Current label record</param>
/// <returns>Translation of the current label in the language selected on the form</returns>
display str translationInLanguage(DocLabelTable _label)
{
    str retVal;

    if (ShowTranslationInLanguage.text() != '')
    {
        retVal = DocUserDefinedLabelHelper::resolveUserDefinedLabel(_label.GlobalLabelId, ShowTranslationInLanguage.text());            
    }

    return retVal;
}

public void linkActive()
{
    super();

    if (shouldPositionToCallerLabel)
    {
        shouldPositionToCallerLabel = false;
        this.positionToRecord(DocLabelTable::findByLabelId(callerLabelId));
    }
}

