/// <summary>
/// Executes the query.
/// </summary>
public void executeQuery()
{
    QueryBuildDataSource qbds = this.query().dataSourceTable(tableNum(DocLabelTranslation));

    //Forces sorting by line number
    this.query().dataSourceTable(tableNum(DocLabelLanguage)).addSortField(fieldNum(DocLabelLanguage, LineNum));

    qbds.clearRanges();

    // If we are in details view and Show labels filter above the translations
    // grid is set to Translated or Not translated, then filter the translations
    // accordingly.
    if (DetailsPanel.isActivePage())
    {
        switch (TranslationFilterCombo.selection())
        {
            case DocLabelTranslationFilter::Translated:
                qbds.addRange(fieldNum(DocLabelTranslation, Translation)).value(SysQuery::valueNotEmptyString());
                break;

            case DocLabelTranslationFilter::NotTranslated:
                qbds.addRange(fieldNum(DocLabelTranslation, Translation)).value(SysQuery::valueEmptyString());
                break;

            default:
            // do nothing
        }
    }

    super();
}

public void linkActive()
