/// <summary>
/// Shows the system label translation for the current label, if it is based on a system label.
/// </summary>
/// <param name = "_docLabelTranslation">DataSource record</param>
/// <returns>Translation of system label in the given language(s)</returns>
[SysClientCacheDataMethodAttribute(true)]
public display DocLabelTranslationInLanguage systemLabelTranslation(DocLabelTranslation _docLabelTranslation)
{
    DocDataSourceProviderLabelRepository docDSPLabelRep = DocDataSourceProviderLabelRepository::construct(DocLabelResolutionStrategy::Default);

    return docDSPLabelRep.tryResolveD365FOLabel(DocLabelTable.BasedOnSysLabelId, _docLabelTranslation.LanguageId);
}

/// <summary>
/// Shows a check box based on if current user-defined label and system label are equal.
/// </summary>
/// <param name = "_docLabelTranslation">DataSource record</param>
/// <returns>True if user-defined label differs from system label, otherwise false</returns>
[SysClientCacheDataMethodAttribute(true)]
public display DocLabelTranslationDiffersFromSystem differsFromSystem(DocLabelTranslation _docLabelTranslation)
{
    DocDataSourceProviderLabelRepository docDSPLabelRep = DocDataSourceProviderLabelRepository::construct(DocLabelResolutionStrategy::Default);

    return _docLabelTranslation.Translation != docDSPLabelRep.tryResolveD365FOLabel(DocLabelTable.BasedOnSysLabelId, _docLabelTranslation.LanguageId);
}

public void jumpRef()
{
    // When language is clicked, open the translation by language form
    // and get positioned to the exactly the same record as this one.
    Args args = new Args();
    args.caller(element);
    args.record(DocLabelTranslation);

    new MenuFunction(menuItemDisplayStr(DocLabelTranslationByLanguage), MenuItemType::Display).run(args);
}

