public void clicked()
{
    MultiSelectionHelper helper = MultiSelectionHelper::construct();
    helper.parmDatasource(DocLabelTable_ds);

    str selectedLabelIDs = '';
    for(DocLabelTable labelTable = helper.getFirst(); labelTable.RecId; labelTable = helper.getNext())
    {
        if (selectedLabelIDs)
        {
            selectedLabelIDs = selectedLabelIDs + ' ';
        }
        selectedLabelIDs = selectedLabelIDs + labelTable.GlobalLabelId + '@';
    }

    if (!selectedLabelIDs)
    {
        return;
    }

    super();

    FormRun formRun;
    Args args = new Args();
    args.caller(element);
    args.name(formStr(DocLabelAddToPrintSettings));

    args.parm(selectedLabelIDs);

    formRun = ClassFactory.formRunClass(args);
    formRun.init();
    formRun.run();
    formRun.wait();
}

