/// <summary>
/// Loop through all marked records and copy system label translations to user-defined label translations.
/// </summary>
public void clicked()
{
    MultiSelectionHelper multiSelectionHelper = MultiSelectionHelper::construct();
    multiSelectionHelper.parmDatasource(DocLabelTranslation_ds);

    DocLabelTranslation selectedDocLabelTranslation = multiSelectionHelper.getFirst();

    DocDataSourceProviderLabelRepository docDSPLabelRep = DocDataSourceProviderLabelRepository::construct(DocLabelResolutionStrategy::Default);

    try
    {
        while (selectedDocLabelTranslation)
        {
            ttsbegin;

            selectedDocLabelTranslation.Translation = docDSPLabelRep.tryResolveD365FOLabel(DocLabelTable.BasedOnSysLabelId, selectedDocLabelTranslation.LanguageId);
            selectedDocLabelTranslation.update();

            ttscommit;

            selectedDocLabelTranslation = multiSelectionHelper.getNext();
        }
    }
    catch
    {
        DocGlobalHelper::handleException(funcName(), "@DocentricAX4:ErrorCopyingSysLabelTranslations");
    }

    DocLabelTranslation_ds.research(true);

    super();
}

