using Docentric.AX.Storage.Azure.BlobStorage;
using UL = Docentric.AX.Framework.Utilities;

/// <summary>
/// Form Docentric AX parameters implementation
/// </summary>
[Form]
public class DocParameters extends FormRun
{
    Set licenseNotificationSettingsSet;

    // SharePoint integration user credentials for Online and On-premises authorization type
    str spIntegrationUserOnlineUsername;
    str spIntegrationUserOnlinePassword;
    str spIntegrationUserOnpremUsername;
    str spIntegrationUserOnpremPassword;
    DocSharePointAuthorizationType lastAuthorizationType;

}

/// <summary>
/// Form initialization
/// </summary>
public void init()
{
    super();

    DocParameters::find();
    DocDocumentRoutingParameters::find();
    DocLicenseParameters::find();

    // Set value of unbound form controls.
    DocentricAxFrameworkVersion.text(DocConstantLicVer::DocentricAxVersion());
    DocentricAxFrameworkReleaseDate.dateValue(DocConstantLicVer::DocentricAxReleaseDate());
    DocentricAxFrameworkInstallationId.text(DocInstallation::getInstallationId());
    HowToManualsUrl.text(DocConstant::HowToManualsURL);
    PrivacyPolicyURL.text(DocConstant::PrivacyPolicyURL);
    TroubleshootingTechniquesArticleUrl.text(DocConstant::TroubleshootingEmailTechniquesURL);

    // SMTP unbound controls.
    this.refreshEmailUnboundControls();

    // Hide/Show the Document routing tab.
    TOCPageDocumentRouting.visible(!DocConstantGlobal::isOnPremEnvironment());

    // Hide/Show the FileSystem group on Print destination settings tab.
    FilePDSaveToFileSystemGroup.visible(DocConstantGlobal::isOneBoxEnvironment() || DocConstantGlobal::isOnPremEnvironment());

    // Hide pre-release features if they are not enabled.
    DocFeaturePreview::applyFeaturePreviewToFormRun(this);

    // Refresh template custom fonts controls
    this.refreshTemplateCustomFontsControls();

    // Toggle SharePoint controls based on authorization type
    this.toggleSharePointControls(DocParameters.SharePointAuthorizationType);

    // Hide features (add-ons) if they are not installed
    DocParameters::applyFeatureToFormRun(this);
}

/// <summary>
/// Change the active tab in table of content
/// </summary>
public void run()
{
    super();

    if (this.args() && this.args().parm())
    {
        if (this.args().parm() == 'SetLicense')
        {
            //TOCPageLicensing.activatePage();
            TOCTab.tab(2);

            // Parameter 'SetLicense' is set on DocParametersLicensing menu item, which appears
            // on the license expiration notification form and on the Workspace form > Manage license subscription. 
            // If we came from the notification we want to automatically open the 'Request renewal' form.
            if (this.args().parmObject() != null && this.args().parmObject() is SystemNotificationExecuteActionDataContract)
            {
                Args args = new Args();
                args.caller(element);
                args.record(DocLicenseParameters);

                new MenuFunction(menuItemDisplayStr(DocLicenseRequestRenewal), MenuItemType::Display).run(args);
            }
        }
        else if (this.args().parm() == 'Emailing')
        {
            TOCPageEmailing.activatePage();
        }
        else if (this.args().parm() == 'PrintArchive')
        {
            TOCPagePrintArchive.activatePage();
        }
        else if (this.args().parm() == 'RestrictPrinters')
        {
            TOCPagePrintDestSettings.activatePage();
            FilePrintDestinationTabPage.collapse();
            PrinterPrintDestinationTabPage.expand();
        }
    }
}

/// <summary>
/// Refresh email unbound controls.
/// </summary>
public void refreshEmailUnboundControls()
{
    SysEmailParameters par = SysEmailParameters::find();
    str defaultEmailProviderText = par.MailerNonInteractive;

    switch (defaultEmailProviderText)
    {
        case DocConstant::EmailProviderSMTP:
            if (par.SMTPUserName)
            {
                defaultEmailProviderText = strFmt('%1: %2', defaultEmailProviderText, par.SMTPUserName);
            }
            else if (par.SMTPUserName == '' && par.SMTPRelayServerName != '')
            {
                // From the 3.3.3 version:
                // If the username is not set but the outgoing mail server is, we will make an exception
                // here and as the default sender address we will use the current user email.
                // This can be a real-world scenario in on-premises environments or when using smtp4dev in non-production.
                defaultEmailProviderText = strFmt("@DocentricAX2:EmailProviderAuthenticationNotRequired", defaultEmailProviderText);
            }
            else
            {
                defaultEmailProviderText = strFmt("@DocentricAX2:DefaultEmailSenderNotSet", defaultEmailProviderText);
            }
            TestConnectionForDefaultAccount.enabled(true);
            break;

        case DocConstant::EmailProviderExchange:
            defaultEmailProviderText = strFmt("@DocentricAX2:DefaultEmailSenderCUA", defaultEmailProviderText);
            TestConnectionForDefaultAccount.enabled(false);
            break;

        case DocConstant::EmailProviderGraph:
            defaultEmailProviderText = strFmt("@DocentricAX2:DefaultEmailSenderCUA", defaultEmailProviderText);
            TestConnectionForDefaultAccount.enabled(false);
            break;

        default:
            defaultEmailProviderText = "@DocentricAX2:DefaultEmailSenderUnknown";
            TestConnectionForDefaultAccount.enabled(false);
    }

    DefaultEmailProvider.text(defaultEmailProviderText);
}

