/// <summary>
/// Refresh data of the calling form.
/// </summary>
/// <param name = "_taskId">Task ID to be executed</param>
/// <returns>Not documented by Microsoft.</returns>
public int task(int _taskId)
{
    #Task
    int ret;

    ret = super(_taskId);

    if (_taskId == #taskRefresh)
    {
        // Force refreshing the unbound fields
        this.refreshEmailUnboundControls();
    }

    return ret;
}

/// <summary>
/// Refresh controls and update fonts over all AOS instances.
/// </summary>
/// <param name = "_refreshAOSInstances">True if you need to refresh the fonts over all AOS instances.</param>
public void refreshTemplateCustomFontsControls(boolean _refreshAOSInstances = false)
{
    boolean useCustomFonts = DocParameters.UseCustomBlobStorageFonts == NoYes::Yes;

    // Define default value
    if ((useCustomFonts) && (System.String::IsNullOrWhiteSpace(DocParameters.CustomFontsBlobStorageContainer)))
    {
        DocParameters.CustomFontsBlobStorageContainer = DocConstant::CustomBlobStorageFontsContainer;
    }

    if (_refreshAOSInstances)
    {
        // Validate Azure storage template fonts container name if set.
        if (useCustomFonts && DocParameters.CustomFontsBlobStorageContainer != '' &&
                !DocAzureBlobHelper::isContainerNameValid(DocParameters.CustomFontsBlobStorageContainer))
        {
            DocGlobalHelper::handleError(strFmt("@DocentricAX:InvalidContainerName", DocParameters.CustomFontsBlobStorageContainer));
            return;
        }

        // Save new state of FontManager
        DocFontManager::updateVersionId(System.Guid::NewGuid());
    }
}

/// <summary>
/// Toggles SharePoint controls based on the authorization type.
/// </summary>
/// <param name = "_authorizationType">The autorization type used to toggle form controls.</param>
public void toggleSharePointControls(DocSharePointAuthorizationType _authorizationType)
{
    IntegrationUsers.visible(_authorizationType == DocSharePointAuthorizationType::Integrated);
    OnlineOnPremisesCredentials.visible(_authorizationType != DocSharePointAuthorizationType::Integrated);

    if (_authorizationType == DocSharePointAuthorizationType::Online)
    {
        OnlineOnPremisesCredentials.caption("@DocentricAX4:SharePointSecuritySettingsOnlineCredentials");
    }
    else if (_authorizationType == DocSharePointAuthorizationType::OnPremise)
    {
        OnlineOnPremisesCredentials.caption("@DocentricAX4:SharePointSecuritySettingsOnPremisesCredentials");
    }

    DocParameters_SharePointUsername.mandatory(_authorizationType != DocSharePointAuthorizationType::Integrated);
    DocParameters_SharePointPassword.mandatory(_authorizationType != DocSharePointAuthorizationType::Integrated);

    DocParameters_ds.object(fieldNum(DocParameters, SharePointUserReader)).allowEdit(_authorizationType == DocSharePointAuthorizationType::Integrated);
    DocParameters_ds.object(fieldNum(DocParameters, SharePointReportUser)).allowEdit(_authorizationType == DocSharePointAuthorizationType::Integrated);
    DocParameters_ds.object(fieldNum(DocParameters, SharePointUserWriter)).allowEdit(_authorizationType == DocSharePointAuthorizationType::Integrated);
    DocParameters_ds.object(fieldNum(DocParameters, SharePointUsername)).allowEdit(_authorizationType != DocSharePointAuthorizationType::Integrated);
}

/// <summary>
/// Initialize the variables used for the logic of storing and displaying the SharePoint 
/// integration user credentials.
/// </summary>
public void initSharePointIntegrationUserValues()
{
    // Initial calue for the last (previous) authorization type,
    // used when switching between the different types.
    lastAuthorizationType = DocParameters.SharePointAuthorizationType;

    // Initialize the variables for Online and On-prem credentials.
    if (DocParameters.SharePointAuthorizationType == DocSharePointAuthorizationType::Online)
    {
        spIntegrationUserOnlineUsername = DocParameters.SharePointUsername;
        spIntegrationUserOnlinePassword = DocParameters.SharePointPassword;
    }
    else if (DocParameters.SharePointAuthorizationType == DocSharePointAuthorizationType::OnPremise)
    {
        spIntegrationUserOnpremUsername = DocParameters.SharePointUsername;
        spIntegrationUserOnpremPassword = DocParameters.SharePointPassword;
    }
}

/// <summary>
/// Update the values used for handling the SharePoint integration user credentials.
/// </summary>
public void updateSharePointIntegrationUserValues()
{
    // Store the last values into the credential variables (if changing from Online or OnPremise).
    if (lastAuthorizationType == DocSharePointAuthorizationType::OnPremise)
    {
        spIntegrationUserOnpremUsername = DocParameters.SharePointUsername;
        spIntegrationUserOnpremPassword = DocParameters.SharePointPassword;
    }
    else if (lastAuthorizationType == DocSharePointAuthorizationType::Online)
    {
        spIntegrationUserOnlineUsername = DocParameters.SharePointUsername;
        spIntegrationUserOnlinePassword = DocParameters.SharePointPassword;
    }

    // Update the displayed values
    if (DocParameters.SharePointAuthorizationType == DocSharePointAuthorizationType::Integrated)
    {
        // Reset the displayed values
        DocParameters.SharePointUsername = '';
        DocParameters.SharePointPassword = '';
    }
    else if (DocParameters.SharePointAuthorizationType == DocSharePointAuthorizationType::Online)
    {
        // Display the values from the Online credential variables.
        DocParameters.SharePointUsername = spIntegrationUserOnlineUsername;
        DocParameters.SharePointPassword = spIntegrationUserOnlinePassword;
    }
    else if (DocParameters.SharePointAuthorizationType == DocSharePointAuthorizationType::OnPremise)
    {
        // Display the values from the On-prem credential variables.
        DocParameters.SharePointUsername = spIntegrationUserOnpremUsername;
        DocParameters.SharePointPassword = spIntegrationUserOnpremPassword;
    }

    lastAuthorizationType = DocParameters.SharePointAuthorizationType;
}

