/// </summary>
public void modified()
{
    DocUserPrinterMode previousValue = DocParameters.orig().UserPrinterMode;
    if (previousValue == DocUserPrinterMode::UserFavoritePrinters)
    {
        if (!Box::yesNoOnce('', "@DocentricAX4:ModifyUserPrinterModePrompt", DialogButton::No))
