/// <summary>
/// Called when the DocEmailAccont record becomes active.
/// </summary>
/// <returns>True/False</returns>
public int active()
{
    int ret;

    ret = super();

    // Set the AllowEdit and Mandatory flags for the fields below depending on the AuthenticationRequired flag
    DocEmailAccount_ds.object(fieldNum(DocEmailAccount, AccountUserName)).mandatory(DocEmailAccount.AuthenticationRequired);
    DocEmailAccount_ds.object(fieldNum(DocEmailAccount, AccountUserName)).allowEdit(DocEmailAccount.AuthenticationRequired);
    AdditionalEmailAccountsGrid_passwordEdit.allowEdit(DocEmailAccount.AuthenticationRequired);

    return ret;
}

/// <summary>
/// Called when the value is modified.
/// </summary>
public void modified()
{
    if (!DocEmailAccount.AuthenticationRequired)
    {
        // Clear the credentials if the user confirms setting the AuthenticationRequired flag to false
        if (!Box::yesNo("@ApplicationFoundation:SysMailerSMTPAuthenticationClearConfirmationText", DialogButton::No))
        {
            DocEmailAccount.AuthenticationRequired = true;
            return;
        }

        DocEmailAccount.AccountUserName = '';
        DocEmailAccount.passwordEdit(true, '');
    }
    else
    {
        if (!DocEmailAccount.AccountUserName)
        {
            DocEmailAccount.AccountUserName = DocEmailAccount.UserName;
        }
    }

    super();

    DocEmailAccount_ds.active();
    DocEmailAccount_ds.research(true);
}

/// <summary>
/// Called when the value is modified.
/// </summary>
public void modified()
{
    if (DocEmailAccount.AuthenticationRequired && !DocEmailAccount.AccountUserName)
    {
        DocEmailAccount.AccountUserName = DocEmailAccount.UserName;
    }

    super();
}

public void lookup(FormControl _formControl, str _filterStr)
{
    if (DocPrintArchiveSettings.CompanyId)
    {
        changeCompany(DocPrintArchiveSettings.CompanyId)
        {
            DocuType::lookupDocuActionFile_DC(_formControl);
        }
    }
    else
    {
        DocGlobalHelper::handleWarning("@DocentricAX2:SelectCompanyFirst");
    }
}

public void jumpRef()
{
    if (DocPrintArchiveSettings.CompanyId)
    {
        // change company, open form in modal mode and then revert it
        CompanyId curCompanyId = curExt();
        appl.setDefaultCompany(DocPrintArchiveSettings.CompanyId, false);       // change company without warning

        try
        {
            // Standard sets args.record() which shows just one record on opened form.
            // We open the form from code using args.lookupRecord() which shows all records but selects the given one.
            Args args = new Args();
            args.lookupRecord(DocuType::find(DocPrintArchiveSettings.DocuTypeId));
            args.caller(element);

            FormRun formRun = new MenuFunction(menuItemDisplayStr(DocuType), MenuItemType::Display).create(args);
            formRun.run();
            formRun.wait(true);
        }
        finally
        {
            appl.setDefaultCompany(curCompanyId, false);
        }
    }
    else
    {
        DocGlobalHelper::handleWarning("@DocentricAX2:SelectCompanyFirst");
    }
}

/// <summary>
/// Executes the query.
/// </summary>
public void executeQuery()
{
    super();

    // Show/Hide license fields.
    this.showHideLicenseFields();

    // Set expiration images if needed.
    if (DocLicenseParameters.LicenseType != DocReportParamLicenseType::FreeEdition)
    {       
        if (DocLicenseParameters.LicenseSubscriptionEnd == dateNull() ||
            DocLicenseParameters.LicenseSubscriptionEnd < DateTimeUtil::getToday(DateTimeUtil::getUserPreferredTimeZone()))
        {
            SubscriptionExpired.visible(true);
        }
        else
        {
            SubscriptionExpired.visible(false);
        }

        DocLicenseParameters_ds.object(fieldnum(DocLicenseParameters, LicenseExpirationDate)).visible(DocLicenseParameters.LicenseExpirationDate == dateMax());
    }

    // SendDiagnosticsData.
    if (DocLicenseParameters.LicenseType == DocReportParamLicenseType::FreeEdition)
    {
        DocParameters.UseAppInsight = true;
        SendDiagnosticsData.allowEdit(false);
        SendDiagnosticsData.helpText("@DocentricAX:SendDiagnosticsDataDescFreeEdition");
    }
    else
    {
        SendDiagnosticsData.allowEdit(true);
        SendDiagnosticsData.helpText("@DocentricAX:SendDiagnosticsDataDesc");
    }
}

/// <summary>
/// Show or hide license fields based on the license type.
/// </summary>
private void showHideLicenseFields()
{
    boolean show = DocLicenseParameters.LicenseType != DocReportParamLicenseType::FreeEdition;

    // The following group contains 5 groups, which needs to be in separate columns. The selected pattern
    // does not support setting the number of columns and column mode, so this has to be set in code.
    LicensingGroup.columns(6, ColumnsMode::Fixed);

    DocLicenseRequestRenewal.visible(show);

    LicenseExpirationGroup.visible(show);
    SubscriptionInfoGroup.visible(show);
    RenewalRequestInfoGroup.visible(show);

    LicenseInfo_LicenseSerialNo.visible(show);
    LicenseInfo_LicenseHolder.visible(show);
    LicenseInfo_LicenseCode.visible(show);
    LicenseInfo_LicenseDetails.visible(show);

    LicenseNotificationSettingsGrid.visible(show);

    DocRemoveLicense.visible(show);
    DocLicenseExport.visible(show);
}

/// <summary>
/// Initializes the data source.
/// </summary>
public void init()
{
    super();

    // Fill the temporary table with the license notification settings stored in the 
    // container field LicesneNotificationSettings in the DocLicenseParameters table
    licenseNotificationSettingsSet = DocLicenseNotificationSettingsTmp.populate();
}

/// <summary>
/// Called when the record becomes active.
/// </summary>
/// <returns>>A record number</returns>
public int active()
{
    int ret;

    ret = super();

    // Allow editing the email recipients fields depending on the selected notification security role 
    LicenseNotificationSettingsGrid_notificationEmailToEdit.allowEdit(DocLicenseNotificationSettingsTmp.NotificationRole == DocLicenseNotificationRole::None);
    LicenseNotificationSettingsGrid_notificationEmailToEdit.mandatory(
        DocLicenseNotificationSettingsTmp.NotificationRole == DocLicenseNotificationRole::None && DocLicenseNotificationSettingsTmp.NotificationSendEmail);

    return ret;
}

/// <summary>
/// Writes a record to the table.
/// </summary>
public void write()
{
    super();

    // Write the license notification setting changes back to the container field 
    // LicesneNotificationSettings in the DocLicenseParameters table
    DocLicenseNotificationSettingsTmp.updateNotificationSettings(licenseNotificationSettingsSet);
    DocLicenseParameters.LicenseNotificationSettings = licenseNotificationSettingsSet.pack();
    DocLicenseParameters.write();
}

/// <summary>
/// Validates the saving record before the save operation.
/// </summary>
/// <returns>True, if there is no validation errors, otherwise returns false.</returns>
public boolean validateWrite()
{
    if (DocLicenseNotificationSettingsTmp.NotificationRole == DocLicenseNotificationRole::None &&
        DocLicenseNotificationSettingsTmp.NotificationSendEmail)
    {
        if (DocLicenseNotificationSettingsTmp.NotificationEmailTo == '')
        {
            DocGlobalHelper::handleWarning("@DocentricAX4:EmailRecipientsNotSetErrMsg");
            return false;
        }
        else if (!DocGlobalHelper::validateEmailList(DocLicenseNotificationSettingsTmp.NotificationEmailTo))
        {
            DocGlobalHelper::handleWarning("@DocentricAX4:EmailRecipientsInvalidErrMsg");
            return false;
        }
    }

    return super();
}

/// <summary>
/// Marks current environment with light blue color, others are white.
/// </summary>
/// <param name = "_record">DataSource record</param>
/// <param name = "_options">Row display options</param>
public void displayOption(Common _record, FormRowDisplayOption _options)
{
    DocEnvironmentSettings currentRecord = _record.data();

    if (currentRecord.currentEnvironment())
    {
        _options.backColor(0xFFF4CE);
    }
    else
    {
        _options.backColor(0xFFFFFF);
    }

    super(_record, _options);
}

