/// <summary>
/// Called when the DocLoadLicense menu button is clicked.
/// </summary>
public void clicked()
{
    ModifiedDateTime currentModifiedDateTime = DocLicenseParameters.ModifiedDateTime;

    // Call the main method of the DocLicenseMng class via the menu item DocLoadLicense
    super();

    // Set focus to the LicenseDetails fast tab and revalidate the configured product 
    // add-ons when a license is succesfully loaded
    if (currentModifiedDateTime != DocLicenseParameters.ModifiedDateTime)
    {
        LicenseInfo_LicenseDetails.setFocus();
        DocParameters_ds.object(fieldNum(DocParameters, EnableCbdDs)).modified();
        DocParameters_ds.object(fieldNum(DocParameters, EnableWhsLicensePlateLabels)).modified();
    }
}

