/// <summary>
/// Clicked event on button SharePointEditButton.
/// </summary>
public void clicked()
{
    str hostname = DocSharePointHelperV3::getSharePointHostNameFromUrl(DocParameters.ReportTemplateStorageSharePointSiteUrl);
    if (!hostname)
    {
        hostname = DocSharePointHelperV3::getDefaultServer();
    }
    if (hostname)
    {
        DocSharePointFolderSelection spFolderSelection = DocSharePointFolderSelection::construct(
                                                                                            hostname,
                                                                                            DocParameters.ReportTemplateStorageSharePointSiteUrl,
                                                                                            DocParameters.ReportTemplateStorageSharePointFolderPath);
        if (spFolderSelection && spFolderSelection.promptUser())
        {
            DocParameters.ReportTemplateStorageSharePointSiteUrl = spFolderSelection.parmSelectedSite().Url;
            DocParameters.ReportTemplateStorageSharePointFolderPath = spFolderSelection.getRelativeFolderPath();
        }
    }
    else
    {
        MenuFunction mf = new MenuFunction(menuItemDisplayStr(DocuParameters), MenuItemType::Display);
        DocGlobalHelper::handleError(strfmt("@ApplicationFoundation:DocuType_SharePointNotConfigured", mf.label()), false, funcName());
    }
}

/// <summary>
/// Clicked event on button NavigateToSpSiteUrl.
/// </summary>
public void clicked()
{
    if (DocParameters.ReportTemplateStorageSharePointSiteUrl)
    {
        Browser b = new Browser();

        str sharePointUrlToNavigate = DocFileMngHelper::addUrlDelimiter(DocParameters.ReportTemplateStorageSharePointSiteUrl) +
            DocParameters.ReportTemplateStorageSharePointFolderPath;

        b.navigate(sharePointUrlToNavigate, true);
    }

    super();
}

