/// <summary>
/// Clicked event on button TemplateFontsUploadButton.
/// </summary>
public void clicked()
{
    super();

    Args args = new Args();
    // Setting current record
    args.record(DocParameters_ds.cursor());
    // Setting caller
    args.caller(element);
    // Create new menu item
    MenuFunction mf = new MenuFunction(menuitemDisplayStr(DocCustomBlobStorageUploadFonts), MenuItemType::Display);

    // We need to call form run to check if OK button was clicked
    FormRun formRun = mf.create(args);
    if (formRun)
    {
        // Run the form
        formRun.run();
        // Wait to complete
        formRun.wait();

        // if OK was clicked upload was executed
        if (formRun.closedOk())
        {
            // We need to update controls and FontManager
            element.refreshTemplateCustomFontsControls(true);
        }

        // Detach the formRun from menu item
        formRun.detach();
    }
}

/// <summary>
/// Clicked event on button TemplateFontsDownloadButton.
/// </summary>
public void clicked()
{
    if (!DocParameters.CustomFontsBlobStorageContainer)
    {
        DocGlobalHelper::handleWarning("@DocentricAX3:BlobContainerNotSpecifiedWarn");
        return;
    }

    super();

    Args args = new Args();
    // Setting current record
    args.record(DocParameters_ds.cursor());
    // Setting caller
    args.caller(element);
    // Create new menu item
    MenuFunction mf = new MenuFunction(menuitemActionStr(DocCustomBlobStorageDownloadFonts), MenuItemType::Action);
    // Run the menu item
    mf.run(args);
}

