/// <summary>
/// Clicked event on the TestConnectionForDefaultAccount button.
/// </summary>
public void clicked()
{
    super();

    DocSmtpMailClient smtpMailClient = DocEmailMngHelper::getSmtpClientFromSysParameters();
    DocSysMailerSMTP mailer = DocSysMailerSMTP::construct(smtpMailClient);
    mailer.testConnection_DC(true);
}

/// <summary>
/// Clicked event on the TestConnectionForAccount button.
/// </summary>
public void clicked()
{
    super();

    DocSmtpMailClient smtpMailClient = DocEmailMngHelper::getSmtpClientFromAdditionalAccount(DocEmailAccount);
    DocSysMailerSMTP mailer = DocSysMailerSMTP::construct(smtpMailClient);
    mailer.testConnection_DC(true);
}

/// <summary>
/// Clicked event on checkbox DocParameters_UseDocentricEmailDistributorBatch.
/// </summary>
public void clicked()
{
    super();

    if (!DocParameters.UseDocentricEmailDistributorBatch)
    {
        DocParameters.EnableOutgoingEmailLog = false;
    }
    else
    {
        DocParameters.EnableOutgoingEmailLog = true;
        DocGlobalHelper::handleWarning("@DocentricAX3:TurnOnDocEmailDistributorBatchWarn");
    }

    // Enable/Disable 'Enable Email distributor log'.
    DocParameters_EnableOutgoingEmailLog.allowEdit(DocParameters.UseDocentricEmailDistributorBatch);
}

