{
    DocInstallation::updateIsOnPremEnvironment();

    super();
}

/// <summary>
/// Clicked event on menu funtion button SysCorpNetPrinters.
/// </summary>
public void clicked()
{
    // Work-around to avoid getting the filtered target.
    Args args = new Args();
    args.caller(element);

    new MenuFunction(menuItemDisplayStr(SysCorpNetPrinters), MenuItemType::Display).run(args);
}

/// <summary>
/// Calls lookup control.
/// </summary>
public void lookup()
{
    // Calls the multi-select lookup control.
    // The form string control DocParameters_PrintDestShowTemplateEdit is used to store
    // the list of the selected RecIds from the multi-select lookup control. It is mandatory
    // for proper working of the multi-select lookup control.
    DocParameters::lookupPrintDestShowTemplate(this);
}

/// <summary>
/// Clicked event on menu funtion button DocuParametersLinkMenuButton.
/// </summary>
public void clicked()
{
    FormRun formRun = new MenuFunction(menuItemDisplayStr(DocuParameters), MenuItemType::Display).create();
    formRun.init();
    formRun.run();
    formRun.design().controlName(formControlStr(DocuParameters, SharePoint)).setFocus();
}

/// <summary>
/// Clicked event on menu function button Help.
/// </summary>
public void clicked()
{
    DocGlobalHelper::navigateToUrl(DocConstant::SharePointExtendSecurityManualURL, true, false);
}

/// <summary>
/// Changes when the SharePoint authorization type has changed.
/// </summary>
/// <returns>The new selected item value.</returns>
public int selectionChange()
{
    int ret;
    // execute the change
    ret = super();

    // current selection
    element.toggleSharePointControls(DocParameters.SharePointAuthorizationType);

    // Update the values used for handling the SharePoint integration user credentials
    element.updateSharePointIntegrationUserValues();

    return ret;
}

