/// <summary>
/// Loads the latest saved variable values for the current user in the form.
/// </summary>
private void getSysLastValue()
{
    container packedForm = xSysLastValue::getValue(curext(), curUserId(), UtilElementType::Form, formStr(DocPrintMgmtUtils));

    Version version = conpeek(packedForm, 1);
    if (version == #CurrentVersion)
    {
        [version, #CurrentList] = packedForm;
    }
}

/// <summary>
/// Saves the variable values for the current user in the form.
/// </summary>
private void saveSysLastValue()
{
    xSysLastValue::putValue([#CurrentVersion, #CurrentList], curext(), curUserId(), UtilElementType::Form, formStr(DocPrintMgmtUtils));
}

