public Map getFieldGroups()
{
    return fieldGroupsVisibilityMap;
}

/// <summary>
/// Gets the key value for the Map where field group names and visibilities are stored.
/// Key is formed from the 2-digit index by which the field group appears in the form and
/// the field group name, separated by a dot (for the readability, if needed).
/// Using such keys enables listing the field groups in the same order as they appear
/// in the form. If we would use only field group names, then the field groups  in the lookup
/// would be sorted alphabetically based on their names.
/// </summary>
/// <param name = "_fc">FormControl field group</param>
/// <param name = "_index">int order of appearance of the field group in the form</param>
/// <returns>str key value to be used in the Map.</returns>
private str formControl2Key(FormControl _fc, int _index)
{
    return strRFix(int2str(_index), 2, '0') + '.' + _fc.labelText();
}

/// <summary>
/// Initialize the Map with field group names and their visibilities (true)
/// if we didn't get it from the SysLastValue (first opening of form).
/// Then apply that configuration to the field groups in the form.
/// </summary>
public void initFieldGroupsVisibility()
{
    if (fieldGroupsVisibilityMap == null)
    {
        fieldGroupsVisibilityMap = new Map(Types::String, Types::Enum);

        for (int i = 1; i <= Grid.controlCount(); i++)
        {
            FormControl fc = Grid.controlNum(i);

            str groupName = fc.name();
            if (groupName == 'ValidationStatus')
            {
                // Skip some identification groups (we want to show them always)
                continue;
            }

            // For the first opening we show only the field group with Print mgmt. setting summary,
            // so that the form is as responsive as possible.
            boolean showGroup = (groupName == 'PrintMgmtSetupSummary');
            fieldGroupsVisibilityMap.add(this.formControl2Key(fc, i), showGroup);
        }
    }

    this.setFieldGroupsVisibility(false, fieldGroupsVisibilityMap);
}

/// <summary>
/// Set the visibility of field groups in the main form grid based on the provided Map, 
/// or set all field groups to visible if _all parameter is true.
/// </summary>
/// <param name = "_all">boolean, if true then all field groups are set to visible
/// and the second parameter is ignored. If it is false, then the information 
/// from the second parameter is used to set only the selected field groups
/// to visible.</param>
/// <param name = "_fieldGroups">Map of field group names and visibilities
/// to be set on them. If first parameter is true, then this Map is ignored.
/// It is used only if the first parameter is false.</param>
public void setFieldGroupsVisibility(boolean _all, Map _fieldGroups)
{
    for (int i = 1; i <= Grid.controlCount(); i++)
    {
        FormControl fc = Grid.controlNum(i);
        str mapKey = this.formControl2Key(fc, i);
        if (_all)
        {
            fc.visible(true);
        }
        else if (_fieldGroups.exists(mapKey))
        {
            fc.visible(_fieldGroups.lookup(mapKey));
        }

        // Overwrite the previous key-value par, it is the fastest way to set new visibility value
        // on the existing key.
        if (fieldGroupsVisibilityMap.exists(mapKey))
        {
            fieldGroupsVisibilityMap.add(mapKey, fc.visible());
        } 
    }
}

