/// <summary>
/// Initializes the form.
/// </summary>
public void init()
{
    super();

    // Populate the PrintMgmtReportFormat table for the current company.
    DocPrintMgmtHelper::populatePrintMgmtReportFormat();

    // Get the values stored via SysLastValue framework.
    this.getSysLastValue();
    this.setControlValuesFromSysLastValue();

    // Init field groups visibility if needed.
    this.initFieldGroupsVisibility();

    // Enable/disable diagnostic buttons
    diagnosticFormsAllowedForUser = this.diagnosticsFormsEnabledForUser();
    DiagnosticsCustomerButtonGroup.enabled(diagnosticFormsAllowedForUser.in(DocAccountRole::Customer));
    DiagnosticsVendorButtonGroup.enabled(diagnosticFormsAllowedForUser.in(DocAccountRole::Vendor));

    // Get the license type info
    isLicenseValid = DocLicenseMng::isLicenseValid();
    if (!isLicenseValid)
    {
        DocGlobalHelper::handleWarning("@DocentricAX4:PrintMgmtUtilsFreeEditionStartupWarning");
    }

    // Feature callouts        
    element.addWhatsNewNotifications();
}

