/// <summary>
/// Sets the form controls.
/// </summary>
public void enableControls()
{
    // Enable the SSRS or Docentric file type in Docentric File and Email
    // print destination depending on the template type (Doc/SSRS)
    boolean isDocentricDesign = !DocPrintMgmtUtilsTmp.useSrsBuiltInDesign();
    element.control(element.controlId(formControlStr(DocPrintMgmtUtils, DocentricFile_DestDocFileFormat))).enabled(isDocentricDesign);
    element.control(element.controlId(formControlStr(DocPrintMgmtUtils, DocentricFile_DestDocFileFormatSrs))).enabled(!isDocentricDesign);
    element.control(element.controlId(formControlStr(DocPrintMgmtUtils, DocentricEmail_DestDocEmailAttachmentFileFormat))).enabled(isDocentricDesign);
    element.control(element.controlId(formControlStr(DocPrintMgmtUtils, DocentricEmail_DestDocEmailAttachmentFileFormatSrs))).enabled(!isDocentricDesign);

    // Enable Email templates lookup
    element.control(element.controlId(formControlStr(DocPrintMgmtUtils, DocentricEmail_DestDocEmailTemplateId))).enabled(DocPrintMgmtUtilsTmp.DestDocEmailUseEmailTemplate);

    // Disable the buttons related to editing if record is marked for deletion 
    boolean isRecordMarkedForDeletion = DocPrintMgmtUtilsTmp.RecordStatus == DocPrintMgmtUtilsRecordStatus::MarkForDeletion;
    element.control(element.controlId(formControlStr(DocPrintMgmtUtils, EditSettingsMenuItem))).enabled(!isRecordMarkedForDeletion);
    element.control(element.controlId(formControlStr(DocPrintMgmtUtils, CopyToWorkingCopyMenuItem))).enabled(!isRecordMarkedForDeletion);
    element.control(element.controlId(formControlStr(DocPrintMgmtUtils, ApplyWorkingCopyMenuItem))).enabled(isWorkingCopySet && !isRecordMarkedForDeletion);

    // Set the DestDocSaveProformaToArchive field control based on print management document type
    DocPrintMgmtUtilsTmp_ds.object(fieldNum(DocPrintMgmtUtilsTmp, DestDocSaveProformaToArchive)).enabled(isDocentricDesign && DocPrintMgmtHelper::isProformaSupported(DocPrintMgmtUtilsTmp.DocInstanceDocumentType));
}

/// <summary>
/// Gets a set of Account types for which the current user has a privilege to access.
/// </summary>
/// <returns>Set of Account types.</returns>
private Set diagnosticsFormsEnabledForUser()
{
    Set custVendAccess = new Set(Types::Enum);
    SecurityRights securityRights = SecurityRights::construct();

    AccessRight accessRight = securityRights.menuItemAccessRight(SecurableType::MenuItemDisplay, menuItemDisplayStr(CustTableListPage));
    if (accessRight !=AccessRight::NoAccess)
    {
        custVendAccess.add(DocAccountRole::Customer);
    }

    accessRight = securityRights.menuItemAccessRight(SecurableType::MenuItemDisplay, menuItemDisplayStr(VendTableListPage));
    if (accessRight != AccessRight::NoAccess)
    {
        custVendAccess.add(DocAccountRole::Vendor);
    }

    return custVendAccess;
}

