/// <summary>
/// Sets the text color and the background color for a record in the data source.
/// </summary>
/// <param name = "_record">Current record</param>
/// <param name = "_options">An instance of the FormRowDisplayOption object</param>
public void displayOption(Common _record, FormRowDisplayOption _options)
{
    // Mark dirty records with light yellow color, 
    // records marked for deletion with light red color, 
    // others are white.
    DocPrintMgmtUtilsTmp currentRecord = _record.data();
    if (currentRecord.RecordStatus == DocPrintMgmtUtilsRecordStatus::Dirty)
    {
        _options.backColor(0xFFF4CE);   // yellow color from infolog warning
    }
    else if (currentRecord.RecordStatus == DocPrintMgmtUtilsRecordStatus::MarkForDeletion)
    {
        //_options.backColor(0x999999);   // dark grey color
        _options.backColor(0xFDE7E9);   // light red color from infolog error
    }
    else
    {
        _options.backColor(0xFFFFFF);   // white color
    }

    super(_record, _options);
}

