/// <summary>
/// Called when the focus is moved from the current record.
/// </summary>
/// <param name = "_forceUpdate">Should update the current record</param>
/// <returns>True/False</returns>
public boolean leaveRecord(boolean _forceUpdate = false)
{
    boolean ret;

    ret = super(_forceUpdate);            

    // Make sure that packed and unpacked values are in sync.
    DocPrintMgmtUtilsTmp currentRecord = DocPrintMgmtUtilsTmp::find(DocPrintMgmtUtilsTmp.RecId, tmpTable, true);
    if (currentRecord.UnpackedSyncStatus == DocPrintMgmtUtilsUnpackedSyncStatus::NotSynced)
    {
        ttsbegin;
        DocPrintMgmtUtilsHelper::packAndSavePrintDestinationSettings(currentRecord);
        currentRecord.update();
        ttscommit;

        // Validate the staging record
        boolean isValid = currentRecord.validate();
        if (!isValid)
        {
            DocGlobalHelper::handleWarning("@DocentricAX4:RecordLeaveInvalidRecordWarning");
        }

        // If some modification was made on the record, force marking it as dirty (yellow color).
        this.research(true);
    }

    return ret;
}

/// <summary>
/// Executes the query.
/// </summary>
public void executeQuery()
{            
    QueryBuildDataSource qbds = this.queryBuildDataSource();

    qbds.clearRanges();
    str filterString;

    // Filter based on the Print destination selection
    switch (PrintDestinationFilterCombo.selection())
    {
        case DocPrintMgmtUtilsFilterPrintDestination::AllDocentric:
            filterString = strFmt('((%1.%2 == %3) || (%1.%2 == %4) || (%1.%2 == %5) || (%1.%2 == %6) || (%1.%2 == %7) || (%1.%2 == %8))',
                qbds.name(),
                fieldStr(DocPrintMgmtUtilsTmp, PrintMediumType),
                enum2int(SRSPrintMediumType::GenerateDataSource_DC),
                enum2int(SRSPrintMediumType::Archive_DC),
                enum2int(SRSPrintMediumType::Screen_DC),
                enum2int(SRSPrintMediumType::Printer_DC),
                enum2int(SRSPrintMediumType::File_DC),
                enum2int(SRSPrintMediumType::Email_DC));                                   
            qbds.addRange(fieldNum(DocPrintMgmtUtilsTmp, PrintMediumType)).value(filterString);
            break;

        case DocPrintMgmtUtilsFilterPrintDestination::AllSSRS :
            filterString = strFmt('((%1.%2 == %3) || (%1.%2 == %4) || (%1.%2 == %5) || (%1.%2 == %6) || (%1.%2 == %7))',
                qbds.name(),
                fieldStr(DocPrintMgmtUtilsTmp, PrintMediumType),
                enum2int(SRSPrintMediumType::Archive), 
                enum2int(SRSPrintMediumType::Screen),
                enum2int(SRSPrintMediumType::Printer),
                enum2int(SRSPrintMediumType::File),
                enum2int(SRSPrintMediumType::Email));                    
            qbds.addRange(fieldNum(DocPrintMgmtUtilsTmp, PrintMediumType)).value(filterString);
            break;

        case DocPrintMgmtUtilsFilterPrintDestination::DocentricDS :
            qbds.addRange(fieldNum(DocPrintMgmtUtilsTmp, PrintMediumType)).value(queryValue(enum2int(SRSPrintMediumType::GenerateDataSource_DC)));
            break;

        case DocPrintMgmtUtilsFilterPrintDestination::DocentricArchive :
            qbds.addRange(fieldNum(DocPrintMgmtUtilsTmp, PrintMediumType)).value(queryValue(enum2int(SRSPrintMediumType::Archive_DC)));
            break;

        case DocPrintMgmtUtilsFilterPrintDestination::DocentricScreen :
            qbds.addRange(fieldNum(DocPrintMgmtUtilsTmp, PrintMediumType)).value(queryValue(enum2int(SRSPrintMediumType::Screen_DC)));
            break;

        case DocPrintMgmtUtilsFilterPrintDestination::DocentricPrinter :
            qbds.addRange(fieldNum(DocPrintMgmtUtilsTmp, PrintMediumType)).value(queryValue(enum2int(SRSPrintMediumType::Printer_DC)));
            break;

        case DocPrintMgmtUtilsFilterPrintDestination::DocentricFile :
            qbds.addRange(fieldNum(DocPrintMgmtUtilsTmp, PrintMediumType)).value(queryValue(enum2int(SRSPrintMediumType::File_DC)));
            break;

        case DocPrintMgmtUtilsFilterPrintDestination::DocentricEmail :
            qbds.addRange(fieldNum(DocPrintMgmtUtilsTmp, PrintMediumType)).value(queryValue(enum2int(SRSPrintMediumType::Email_DC)));
            break;

        case DocPrintMgmtUtilsFilterPrintDestination::SSRSArchive :
            qbds.addRange(fieldNum(DocPrintMgmtUtilsTmp, PrintMediumType)).value(queryValue(enum2int(SRSPrintMediumType::Archive)));
            break;

        case DocPrintMgmtUtilsFilterPrintDestination::SSRSScreen :
            qbds.addRange(fieldNum(DocPrintMgmtUtilsTmp, PrintMediumType)).value(queryValue(enum2int(SRSPrintMediumType::Screen)));
            break;

        case DocPrintMgmtUtilsFilterPrintDestination::SSRSPrinter :
            qbds.addRange(fieldNum(DocPrintMgmtUtilsTmp, PrintMediumType)).value(queryValue(enum2int(SRSPrintMediumType::Printer)));
            break;

        case DocPrintMgmtUtilsFilterPrintDestination::SSRSFile :
            qbds.addRange(fieldNum(DocPrintMgmtUtilsTmp, PrintMediumType)).value(queryValue(enum2int(SRSPrintMediumType::File)));
            break;

        case DocPrintMgmtUtilsFilterPrintDestination::SSRSEmail :
            qbds.addRange(fieldNum(DocPrintMgmtUtilsTmp, PrintMediumType)).value(queryValue(enum2int(SRSPrintMediumType::Email)));
            break;
    }

    // Filter based on the Print management module selection
    switch (ModuleFilterCombo.selection())
    {
        case DocPrintMgmtUtilsFilterModule::AccountsPayable :
            qbds.addRange(fieldNum(DocPrintMgmtUtilsTmp, PrintMgmtModule)).value(queryValue(enum2int(PrintMgmtNodeType::Purch)));
            break;

        case DocPrintMgmtUtilsFilterModule::AccountsReceivable :
            qbds.addRange(fieldNum(DocPrintMgmtUtilsTmp, PrintMgmtModule)).value(queryValue(enum2int(PrintMgmtNodeType::Sales)));
            break;

        case DocPrintMgmtUtilsFilterModule::InventoryManagement :
            qbds.addRange(fieldNum(DocPrintMgmtUtilsTmp, PrintMgmtModule)).value(queryValue(enum2int(PrintMgmtNodeType::Invent)));
            break;

        case DocPrintMgmtUtilsFilterModule::ProjectManagementAndAccounting :
            qbds.addRange(fieldNum(DocPrintMgmtUtilsTmp, PrintMgmtModule)).value(queryValue(enum2int(PrintMgmtNodeType::Project)));
            break;

        case DocPrintMgmtUtilsFilterModule::TransportationManagement :
            qbds.addRange(fieldNum(DocPrintMgmtUtilsTmp, PrintMgmtModule)).value(queryValue(enum2int(PrintMgmtNodeType::TMS)));
            break;

        case DocPrintMgmtUtilsFilterModule::WarehouseManagement :
            qbds.addRange(fieldNum(DocPrintMgmtUtilsTmp, PrintMgmtModule)).value(queryValue(enum2int(PrintMgmtNodeType::WHS)));
            break;
    }

    // Filter based on the Node level selection
    switch (NodeLevelFilterCombo.selection())
    {
        case DocPrintMgmtUtilsFilterNodeLevel::ModuleLevel:
            qbds.addRange(fieldNum(DocPrintMgmtUtilsTmp, DocInstanceNodeLevel)).value(queryValue(enum2int(DocPrintMgmtNodeLevel::Module)));
            break;

        case DocPrintMgmtUtilsFilterNodeLevel::AccountLevel:
            qbds.addRange(fieldNum(DocPrintMgmtUtilsTmp, DocInstanceNodeLevel)).value(queryValue(enum2int(DocPrintMgmtNodeLevel::Account)));
            break;

        case DocPrintMgmtUtilsFilterNodeLevel::TransactionLevel:
            qbds.addRange(fieldNum(DocPrintMgmtUtilsTmp, DocInstanceNodeLevel)).value(queryValue(enum2int(DocPrintMgmtNodeLevel::Transaction)));
            break;

        case DocPrintMgmtUtilsFilterNodeLevel::OnlyOverrides:
            qbds.addRange(fieldNum(DocPrintMgmtUtilsTmp, DocInstanceNodeLevel)).value(SysQuery::valueNot(enum2int(DocPrintMgmtNodeLevel::Module)));
            break;                    
    }

    super();
}

