/// <summary>
/// Jumps to the referenced table.
/// </summary>
public void jumpRef()
{
    if (DocPrintMgmtUtilsTmp.NodeSpecificId == '')
    {
        return;
    }

    // Jump to the Account/Transaction on which this override is made  
    DocPrintMgmtNodeInfo nodeInfo = DocPrintMgmtUtilsHelper::getNodeInfo(
                                        DocPrintMgmtUtilsTmp.DocInstanceReferencedTableId, 
                                        DocPrintMgmtUtilsTmp.DocInstanceReferencedRecId,
                                        DocPrintMgmtUtilsTmp.DataAreaId);

    if (nodeInfo.parmIsValid() && nodeInfo.parmReferencedRecord().RecId)
    {
        Args args = new Args();
        args.caller(element);
        args.lookupRecord(nodeInfo.parmReferencedRecord());
        args.formViewOption(FormViewOption::Details);
        new MenuFunction(nodeInfo.parmMenuItemName(), MenuItemType::Display).run(args);
    }
    else
    {
        // No matching record found - this is orphaned Print mgmt setting, warn user about it
        DocGlobalHelper::handleWarning('Orphaned Print management setting, please delete it!');
    }
}

