/// <summary>
/// Called when the control is clicked.
/// </summary>
public void clicked()
{
    super();

    // 'Edit Copy' and 'Paste' buttons are initially disabled,
    // we enable them when 'Copy' is clicked for the first time.
    if (!isWorkingCopySet)
    {
        isWorkingCopySet = true;

        element.control(element.controlId(formControlStr(DocPrintMgmtUtils, EditWorkingCopyMenuItem))).enabled(true);
        element.control(element.controlId(formControlStr(DocPrintMgmtUtils, ApplyWorkingCopyMenuItem))).enabled(true);
    }
}

/// <summary>
/// Called when the control is clicked.
/// </summary>
public void clicked()
{
    super();

    callerPrintMgmtSettingsRecId = DocPrintMgmtUtilsTmp.PrintMgmtSettingsRecId;

    if (DocPrintMgmtUtilsUIHelper::checkAndCleanupNonCleanRecords(tmpTable, DocPrintMgmtUtilsTmp.DocInstanceDocumentType))
    {
        if (DocPrintMgmtUtilsTmp::find(DocPrintMgmtUtilsTmp.RecId, tmpTable))
        {
            callerDocumentType = DocPrintMgmtUtilsTmp.DocInstanceDocumentType;
            DocPrintMgmtUtilsUIHelper::launchPrintMgmtSetup(DocPrintMgmtUtilsTmp, element, true);
        }
        else
        {
            DocGlobalHelper::handleWarning("@DocentricAX4:OpenForDocumentWarning");
            DocPrintMgmtUtilsTmp_ds.research(true);
        }
    }
}

/// <summary>
/// Called when the control is clicked.
/// </summary>
public void clicked()
{
    // If there is a Docentric report setup for this SSRS report, we will open it in detail view mode.
    // Otherwise, we will open the grid mode and get positioned at the top of the grid.
    DocReportTable reportTable = DocReportTable::findReportId(DocPrintMgmtUtilsTmp.FormatName);

    Args args = new Args();
    args.caller(element);
    if (reportTable.RecId)
    {
        args.formViewOption(FormViewOption::Details);
    }            

    FormRun formRun = new MenuFunction(menuItemDisplayStr(DocReportTable), MenuItemType::Display).create(args);
    formRun.run();
    if (reportTable.RecId)
    {
        formRun.dataSource(1).positionToRecord(reportTable);
    }
}

/// <summary>
/// Called when the control is clicked.
/// </summary>
public void clicked()
{
    // If there is in DMF an Export job for the Docentric Print Management Settings Package
    // data entity, open it. Otherwise, open the grid mode with all Export jobs. 
    DMFDefinitionGroup dmfDefinitionGroup;
    DMFDefinitionGroupEntity dmfDefinitionGroupEntity;
    select firstonly dmfDefinitionGroup exists join dmfDefinitionGroupEntity
        where dmfDefinitionGroupEntity.DefinitionGroup == dmfDefinitionGroup.DefinitionGroupName
            && dmfDefinitionGroupEntity.Entity == 'Docentric Print Management Settings Package';            

    Args args = new Args();
    args.caller(element);
    if (dmfDefinitionGroup.RecId)
    {
        args.formViewOption(FormViewOption::Details);
    }

    FormRun formRun = new MenuFunction(menuItemDisplayStr(DMFQuickExportEnhanced), MenuItemType::Display).create(args);
    formRun.run();
    if (dmfDefinitionGroup.RecId)
    {
        formRun.dataSource(1).positionToRecord(dmfDefinitionGroup);
    }
}

