    }
}

/// <summary>
/// Sets the filter query used in the SysQueryForm form.
/// </summary>
private void setFilterQuery()
{
    // Create the original filter query that is used when you click on the Reset button in the SysQueryForm form
    Query filterQueryOrig = new Query();
    filterQueryOrig.title(filterQueryTitle);
    switch (callerMenuItem)
    {
        case menuItemDisplayStr(DocPrintMgmtUtilsCustomersWithoutEmailsDiagResults):
        case menuItemDisplayStr(DocPrintMgmtUtilsCustomersWithInvalidEmailsDiagResults):
            QueryBuildDataSource qbdCustTable = filterQueryOrig.addDataSource(tableNum(CustTable), tableStr(CustTable));
            qbdCustTable.fetchMode(QueryFetchMode::One2One);
            break;

        case menuItemDisplayStr(DocPrintMgmtUtilsVendorsWithoutEmailsDiagResults):
        case menuItemDisplayStr(DocPrintMgmtUtilsVendorsWithInvalidEmailsDiagResults):
            QueryBuildDataSource qbdVendTable = filterQueryOrig.addDataSource(tableNum(VendTable), tableStr(VendTable));
            qbdVendTable.fetchMode(QueryFetchMode::One2One);
            break;
    }
    // Pack the original filter query that is used when you click on the Reset button in the SysQueryForm form
    filterQueryOrigPacked = filterQueryOrig.pack();

    // Count enabled data sources in the original filter query
    filterQueryOrigDataSourceCount = filterQueryOrig.dataSourceCount() - SysQuery::disabledDataSourceSet(filterQueryOrig).elements();

    // Restore the filter query from the SysLastValue table
    this.unpack(xSysLastValue::getValue(curExt(), curUserId(), UtilElementType::Form, this.name(), callerMenuItem));
    if (filterQuery == null)
    {
        // Create the filter query from the original filter query if it was not restored from the SysLastValue table
        filterQuery = new Query(filterQueryOrig);
    }

    // Initialize the 'Records to include' fast tab
    queryHelper = new SRSReportRunBuilder(this, null);
    QueryRun queryRun = new QueryRun(filterQuery);
    queryHelper.updateQueryGroupControl(QueryGroup, queryRun);

    // Set the 'Records to include' tab page caption
    this.setQueryTabPageCaption();
}

/// <summary>
/// Sets the 'Records to include' tab page caption depending on whether the filter query is changed or not.
/// </summary>
private void setQueryTabPageCaption()
{
    QueryTabPage.caption(strFmt('%1%2', "@ProductFramework_Dialog:QueryTabCaption", this.isFilterQueryModified() ? ' (*)' : ''));
}

/// <summary>
/// Gets the flag indicating whether the filter query is changed or not.
/// </summary>
/// <returns>True if the filter query is changed; otherwise false</returns>
private boolean isFilterQueryModified()
{
    // Count enabled data sources in the filter query and check if the filter query has any query ranges or filters 
    int filterQueryDataSourceCount = filterQuery.dataSourceCount() - SysQuery::disabledDataSourceSet(filterQuery).elements();
    boolean hasFilterQueryRangesOrFilters = SysQuery::queryHasRangesOrFilters(filterQuery);

    return filterQueryDataSourceCount != filterQueryOrigDataSourceCount || hasFilterQueryRangesOrFilters;
}

/// <summary>
/// Clears the data sources to empty the grid.
/// </summary>
private void clearDatasources()
{
    switch (callerMenuItem)
    {
        case menuItemDisplayStr(DocPrintMgmtUtilsCustomersWithoutEmailsDiagResults):
            CustTable.clear();
            break;

        case menuItemDisplayStr(DocPrintMgmtUtilsVendorsWithoutEmailsDiagResults):
            VendTable.clear();
            break;

        case menuItemDisplayStr(DocPrintMgmtUtilsCustomersWithInvalidEmailsDiagResults):
        case menuItemDisplayStr(DocPrintMgmtUtilsVendorsWithInvalidEmailsDiagResults):
            DocPartiesEmailContactView.clear();
            break;
    }
}

/// <summary>
/// Builds a form data source to list customers without email contacts.
/// </summary>
private void customersWithoutEmails()
{
    if (!dataSourceInitialized)
    {
        CustTable_ds.queryBuildDataSource().clearRanges();

        QueryBuildDataSource qbdsWE = CustTable_ds.queryBuildDataSource().addDataSource(tableNum(CustTable), 'CustTableWithEmail');
        qbdsWE.addLink(fieldNum(CustTable, RecId), fieldNum(CustTable, RecId));
        qbdsWE.joinMode(JoinMode::NoExistsJoin);

        QueryBuildDataSource qbdsDPL = qbdsWE.addDataSource(tableNum(DirPartyLocation), 'DirPartyLocation');
        qbdsDPL.addLink(fieldNum(CustTable, Party), fieldNum(DirPartyLocation, Party), 'CustTableWithEmail');
        qbdsDPL.joinMode(JoinMode::ExistsJoin);

        QueryBuildDataSource qbdsLEA = qbdsDPL.addDataSource(tableNum(LogisticsElectronicAddress), 'LogisticsElectronicAddress');
        qbdsLEA.addLink(fieldNum(DirPartyLocation, Location), fieldNum(LogisticsElectronicAddress, Location));
        qbdsLEA.addRange(fieldNum(LogisticsElectronicAddress, Type)).value(queryValue(LogisticsElectronicAddressMethodType::Email));
        qbdsLEA.joinMode(JoinMode::ExistsJoin);

        dataSourceInitialized = true;
    }

    Query custTableQuery = new Query(CustTable_ds.query());

    // Copy the filter query to the target query and merge the query filters
    SysQuery::copyDataSource(filterQuery.dataSourceNo(1), CustTable_ds.query().dataSourceNo(1), true);
    SysQuery::mergeFilters(filterQuery, CustTable_ds.query(), 1, true, false);

    CustTable_ds.executeQuery();

    CustTable_ds.query(custTableQuery);

    if (!CustTable.RecId)
    {
        DocGlobalHelper::handleInfo("@DocentricAX4:NoCustomersWithoutEmailAddressMsg");
    }
}

/// <summary>
/// Builds a form data source to list vendors without email contacts.
/// </summary>
private void vendorsWithoutEmails()
{
    if (!dataSourceInitialized)
    {
        VendTable_ds.queryBuildDataSource().clearRanges();

        QueryBuildDataSource qbdsWE = VendTable_ds.queryBuildDataSource().addDataSource(tableNum(VendTable), 'VendTableWithEmail');
        qbdsWE.addLink(fieldNum(VendTable, RecId), fieldNum(VendTable, RecId));
        qbdsWE.joinMode(JoinMode::NoExistsJoin);

        QueryBuildDataSource qbdsDPL = qbdsWE.addDataSource(tableNum(DirPartyLocation), 'DirPartyLocation');
        qbdsDPL.addLink(fieldNum(VendTable, Party), fieldNum(DirPartyLocation, Party), 'VendTableWithEmail');
        qbdsDPL.joinMode(JoinMode::ExistsJoin);

        QueryBuildDataSource qbdsLEA = qbdsDPL.addDataSource(tableNum(LogisticsElectronicAddress), 'LogisticsElectronicAddress');
        qbdsLEA.addLink(fieldNum(DirPartyLocation, Location), fieldNum(LogisticsElectronicAddress, Location));
        qbdsLEA.addRange(fieldNum(LogisticsElectronicAddress, Type)).value(queryValue(LogisticsElectronicAddressMethodType::Email));
        qbdsLEA.joinMode(JoinMode::ExistsJoin);

        dataSourceInitialized = true;
    }

    // Store the original target query
    Query vendTableQuery = new Query(VendTable_ds.query());

    // Copy the filter query to the target query and merge the query filters
    SysQuery::copyDataSource(filterQuery.dataSourceNo(1), VendTable_ds.query().dataSourceNo(1), true);
    SysQuery::mergeFilters(filterQuery, VendTable_ds.query(), 1, true, false);

    // Execute the target query with included filter query
    VendTable_ds.executeQuery();

    // Restore the original target query
    VendTable_ds.query(vendTableQuery);

    if (!VendTable.RecId)
    {
        DocGlobalHelper::handleInfo("@DocentricAX4:NoVendorsWithoutEmailAddressMsg");
    }
}

/// <summary>
/// Builds data source for invalid Email contacts for both, customers and vendors.
/// </summary>
private void invalidEmailContacts()
{
    str message;

    if (!dataSourceInitialized)
    {
        DocPartiesEmailContactView_ds.queryBuildDataSource().clearRange(fieldNum(DocPartiesEmailContactView, RecId));
        DocPrintMgmtUtilsEmailsDiagTmp_ds.queryBuildDataSource().addLink(fieldNum(DocPartiesEmailContactView, RecId), fieldNum(DocPrintMgmtUtilsEmailsDiagTmp, RefRecId));
        dataSourceInitialized = true;
    }

    // Store the original target query
    Query docPartiesEmailContactViewQuery = new Query(DocPartiesEmailContactView_ds.query());

    if (this.args().menuItemName() == menuItemDisplayStr(DocPrintMgmtUtilsCustomersWithInvalidEmailsDiagResults))
    {
        // Copy the filter query to the target query and merge the query filters
        SysQuery::copyDataSource(filterQuery.dataSourceNo(1), DocPartiesEmailContactView_ds.query().dataSourceName(tableStr(CustTable)), true);
        DocSysQuery::mergeFilters(filterQuery, DocPartiesEmailContactView_ds.query(), 1, 2, true, false);

        // Set the join mode between included filter query and the root data source of the target query
        DocPartiesEmailContactView_ds.query().dataSourceName(tableStr(CustTable)).joinMode(JoinMode::ExistsJoin);

        message = "@DocentricAX4:NoInvalidEmailAddressesForCustomersMsg";
    }
    else if (this.args().menuItemName() == menuItemDisplayStr(DocPrintMgmtUtilsVendorsWithInvalidEmailsDiagResults))
    {
        // Copy the filter query to the target query and merge the query filters
        SysQuery::copyDataSource(filterQuery.dataSourceNo(1), DocPartiesEmailContactView_ds.query().dataSourceName(tableStr(VendTable)), true);
        DocSysQuery::mergeFilters(filterQuery, DocPartiesEmailContactView_ds.query(), 1, 2, true, false);

        // Set the join mode between included filter query and the root data source of the target query
        DocPartiesEmailContactView_ds.query().dataSourceName(tableStr(VendTable)).joinMode(JoinMode::ExistsJoin);

        message = "@DocentricAX4:NoInvalidEmailAddressesForVendorsMsg";
    }

    // Execute the target query with included filter query
    DocPartiesEmailContactView_ds.executeQuery();

    // Restore the original target query
    DocPartiesEmailContactView_ds.query(docPartiesEmailContactViewQuery);

    if (!DocPrintMgmtUtilsEmailsDiagTmp.RefRecId)
    {
        DocGlobalHelper::handleInfo(message);
    }
}

/// <summary>
/// Packs an instance of this form as container.
/// </summary>
/// <returns>Packed container</returns>
public container pack()
{
    filterQueryPacked = filterQuery.pack();

    return [CurrentVersion, #CurrentList];
}

/// <summary>
/// Unpacks an instance of this form from container.
/// </summary>
/// <param name = "_packedForm">Packed container</param>
/// <returns>True if unpacking was successful; otherwise false</returns>
public boolean unpack(container _packedForm)
{
    int version = conPeek(_packedForm, 1);
    switch (version)
    {
        case CurrentVersion:
            [version, #CurrentList] = _packedForm;
            break;
        default:
            return false;
    }

    filterQuery = new Query(filterQueryPacked);

    return true;
}

