/// <summary>
/// Initializes the data source.
/// </summary>
public void init()
{
    super();

    this.queryBuildDataSource().addRange(fieldNum(DocPartiesEmailContactView, PartyDataAreaId)).value(queryValue(curExt()));
    this.queryBuildDataSource().addSortField(fieldNum(DocPartiesEmailContactView, AccountNum));

    if (element.args().menuItemName() == menuItemDisplayStr(DocPrintMgmtUtilsCustomersWithInvalidEmailsDiagResults))
    {
        // Link to the customers table
        this.queryBuildDataSource().addRange(fieldNum(DocPartiesEmailContactView, AccountRole)).value(queryValue(DocAccountRole::Customer));

        QueryBuildDataSource qbdCustTable = this.queryBuildDataSource().addDataSource(tableNum(CustTable), tableStr(CustTable));
        qbdCustTable.addLink(fieldNum(DocPartiesEmailContactView, AccountNum), fieldNum(CustTable, AccountNum));
        qbdCustTable.joinMode(JoinMode::ExistsJoin);
        qbdCustTable.fetchMode(QueryFetchMode::One2One);
    }
    else if (element.args().menuItemName() == menuItemDisplayStr(DocPrintMgmtUtilsVendorsWithInvalidEmailsDiagResults))
    {
        // Link to the vendors table
        this.queryBuildDataSource().addRange(fieldNum(DocPartiesEmailContactView, AccountRole)).value(queryValue(DocAccountRole::Vendor));

        QueryBuildDataSource qbdVendTable = this.queryBuildDataSource().addDataSource(tableNum(VendTable), tableStr(VendTable));
        qbdVendTable.addLink(fieldNum(DocPartiesEmailContactView, AccountNum), fieldNum(VendTable, AccountNum));
        qbdVendTable.joinMode(JoinMode::ExistsJoin);
        qbdVendTable.fetchMode(QueryFetchMode::One2One);
    }
}

