/// Executes the query.
/// </summary>
public void executeQuery()
{
    // If the data source is not initialized the query should not be executed.
    if (dataSourceInitialized)
    {
        super();
    }
}

/// <summary>
/// Jumps to the referenced form.
/// </summary>
public void jumpRef()
{
    // When AccountNum field is clicked, open the customer or vendor form
    // and get positioned to the exactly the same record as this one.

    Args args = new Args();
    args.caller(element);

    if (DocPartiesEmailContactView.AccountRole == DocAccountRole::Customer)
    {
        args.record(CustTable::find(DocPartiesEmailContactView.AccountNum));
        args.formViewOption(FormViewOption::Details);

        new MenuFunction(menuItemDisplayStr(CustTableListPage), MenuItemType::Display).run(args);
    }
    else if (DocPartiesEmailContactView.AccountRole == DocAccountRole::Vendor)
    {
        args.record(VendTable::find(DocPartiesEmailContactView.AccountNum));
        args.formViewOption(FormViewOption::Details);

        new MenuFunction(menuItemDisplayStr(VendTableListPage), MenuItemType::Display).run(args);
    }
}

[Hookable(false)]
public void clicked()
{
    // Prepare the filter query and open the SysQueryForm form
    Query query = filterQuery;
    SysQueryRun queryRun = new SysQueryRun(query);
    queryRun.origQueryPack(filterQueryOrigPacked);
    queryRun.saveUserSetup(false);

    if (!queryRun.prompt())
    {
        return;
    }

    // Update the controls in the 'Records to include' fast tab depending on 
    // selected query criteria in the SysQueryForm form
    queryHelper.updateQueryGroupControl(QueryGroup, queryRun);
    filterQuery = queryRun.query();

    // Clear the data sources to empty the grid
    element.clearDatasources();

    // Set the 'Records to include' tab page caption
    element.setQueryTabPageCaption();
}

/// <summary>
/// Depending on the caller menu item, run check for customers/vendors without Email contact
/// or check for invalid customer/vendor Email contacts.
/// </summary>
public void clicked()
{
    switch (callerMenuItem)
    {
        case menuItemDisplayStr(DocPrintMgmtUtilsCustomersWithoutEmailsDiagResults):
            element.customersWithoutEmails();
            break;

        case menuItemDisplayStr(DocPrintMgmtUtilsCustomersWithInvalidEmailsDiagResults):
            DocPrintMgmtUtilsEmailsDiagTmp::getCustomerInvalidEmails(DocPrintMgmtUtilsEmailsDiagTmp);
            element.invalidEmailContacts();
            break;

        case menuItemDisplayStr(DocPrintMgmtUtilsVendorsWithoutEmailsDiagResults):
            element.vendorsWithoutEmails();
            break;

        case menuItemDisplayStr(DocPrintMgmtUtilsVendorsWithInvalidEmailsDiagResults):
            DocPrintMgmtUtilsEmailsDiagTmp::getVendorInvalidEmails(DocPrintMgmtUtilsEmailsDiagTmp);
            element.invalidEmailContacts();
            break;

        default:
            DocGlobalHelper::handleException(strFmt('Invalid caller menu item (%1)', callerMenuItem));
    }

    // Store the filter query to the SysLastValue table
    xSysLastValue::putValue(element.pack(), curExt(), curUserId(), UtilElementType::Form, element.name(), callerMenuItem);
}
