[Form]
public class DocReportSharePointSettings extends FormRun implements IDocRefreshable
{
}

/// <summary>
/// Initializes the form.
/// </summary>
public void init()
{
    super();

    if (element.args() == null || element.args().dataset() != tableNum(DocReportTable) || !element.args().record())
    {
        throw error('Form has to be called from the Docentric reports form');
    }

    // If there is no valid licence, user will be warned, but we will still let him
    // set up SharePoint metaaata settings. Later in the pipeline we will throw the exception
    // if SharePoint metadata settings are used, but the license is invalid.
    DocLicenseMng::checkLicenseUI(DocConstant::FeatureSharePointMetadataFields, this.name());

    // Subscribe to the EditModeSwiched event
    element.viewEditModeHelper().EditModeSwitched += eventhandler(element.postViewEditModeSwitched);
    // Subscribe to the ViewModeSwiched event
    element.viewEditModeHelper().ViewModeSwitched += eventhandler(element.postViewEditModeSwitched);

    element.postViewEditModeSwitched();

    NavigationListGroup.visible(true);

    // Instruction text was splitted into two labels because it was too long for one label (the limit is 2080 characters)
    MetadataInstructionText.text("@DocentricAX4:SharePointSettingsSharePointMetadataInstructionsText" + "@DocentricAX4:SharePointSettingsSharePointMetadataInstructionsText1");

    element.setGridViewColumnLabels();
}

/// <summary>
/// Updates column labels when form is in grid view mode.
/// </summary>
private void setGridViewColumnLabels()
{
    SaveToSharePoint1_SaveToSharePointIsEnabled.label(strFmt('%1 (%2)', "@DocentricAX4:SharePointSettingsIsEnabled", "@DocentricAX:SaveToSharePoint"));
    SaveToSharePoint1_SaveToSharePointActionOnError.label(strFmt('%1 (%2)', "@DocentricAX4:SharePointSettingsActionOnError", "@DocentricAX:SaveToSharePoint"));
    SaveToAttachments1_SaveToAttachmentsIsEnabled.label(strFmt('%1 (%2)', "@DocentricAX4:SharePointSettingsIsEnabled", "@DocentricAX:SaveToAttachments"));
    SaveToAttachments1_SaveToAttachmentsActionOnError.label(strFmt('%1 (%2)', "@DocentricAX4:SharePointSettingsActionOnError", "@DocentricAX:SaveToAttachments"));
    SaveToPrintArchive1_SaveToPrintArchiveIsEnabled.label(strFmt('%1 (%2)', "@DocentricAX4:SharePointSettingsIsEnabled", "@DocentricAX:SaveToArchive"));
    SaveToPrintArchive1_SaveToPrintArchiveActionOnError.label(strFmt('%1 (%2)', "@DocentricAX4:SharePointSettingsActionOnError", "@DocentricAX:SaveToArchive"));
}

/// <summary>
/// Called when the ViewEdit mode is changed.
/// </summary>
public void postViewEditModeSwitched()
{
    SharePointEditButton.enabled(element.viewEditModeHelper().isInEditMode());

    element.changeValidationGroupBackgroundColor();
}

/// <summary>
/// Changes the backgroud color of the Validation group.
/// </summary>
private void changeValidationGroupBackgroundColor()
{
    if (DocReportSharePointSettings.RecId != 0 && DocReportSharePointSettings.IsValid == DocIsReportValid::Invalid)
    {
        Validation_IsValid.colorScheme(FormColorScheme::RGB);
        Validation_IsValid.backgroundColor(0xFDE7E9);  // red color from infolog error

        DocReportSharePointSettings_ValidationError.colorScheme(FormColorScheme::RGB);
        DocReportSharePointSettings_ValidationError.backgroundColor(0xFDE7E9);  // red color from infolog error
    }
    else
    {
        // Restore the bacground color to the default color (different for view and edit mode)
        Validation_IsValid.colorScheme(FormColorScheme::RGB);
        Validation_IsValid.backgroundColor(element.viewEditModeHelper().isInEditMode() ? 0xFAF9F8 : 0xFFFFFF);

        DocReportSharePointSettings_ValidationError.colorScheme(FormColorScheme::RGB);
        DocReportSharePointSettings_ValidationError.backgroundColor(element.viewEditModeHelper().isInEditMode() ? 0xFAF9F8 : 0xFFFFFF);
    }
}

