    DocReportSharePointSettings_ds.research(true);
}

/// <summary>
/// Called when a record becomes active.
/// </summary>
/// <returns>True/False</returns>
public int active()
{
    int ret;

    ret = super();

    element.changeValidationGroupBackgroundColor();

    return ret;
}

/// <summary>
/// Writes the record to the table.
/// </summary>
public void write()
{
    System.String spDcoumentLibraryUrl = DocReportSharePointSettings.SharePointDocumentLibraryUrl;
    DocReportSharePointSettings.SharePointDocumentLibraryUrl = spDcoumentLibraryUrl.TrimEnd(DocConstantFile::UrlDelimiter);

    super();

    // Validate the SharePoint settings
    DocReportSharePointSettingsUtil::validateSharePointSettings(DocReportSharePointSettings.RecId);
    DocReportSharePointSettings_ds.research(true);
}

/// <summary>
/// Writes the record to the table
/// </summary>
public void write()
{
    super();

    // Validate the SharePoint settings
    DocReportSharePointSettingsUtil::validateSharePointSettings(DocReportSharePointSettings.RecId);
    DocReportSharePointSettings_ds.reread();
    DocReportSharePointSettings_ds.refresh();
    element.changeValidationGroupBackgroundColor();
    DocReportSharePointMetadata_ds.research(true);
}

/// <summary>
/// Calls lookup control.
/// </summary>
/// <param name = "_formControl">Form control</param>
/// <param name = "_filterStr">Filter string</param>
public void lookup(FormControl _formControl, str _filterStr)
{
    // Fill all relevant placeholders for a placeholder lookup
    DocReportPlaceholderTmp docReportPlaceholderTmp;
    DocReportPlaceholderTmp::fillReportSpecificPlaceholders(docReportPlaceholderTmp, DocReportTable.ReportId);
    DocReportPlaceholderTmp::fillStandardPlaceholders(docReportPlaceholderTmp, DocReportTable.ReportId);
    DocReportPlaceholderTmp::updatePlaceholdersWithStartEndSymbol(docReportPlaceholderTmp);

    // Create a lookup control
    DocSysTableLookup docSysTableLookup = DocSysTableLookup::newParameters(tableNum(DocReportPlaceholderTmp), _formControl);

    Query query = new Query();
    QueryBuildDataSource qbds = query.addDataSource(tableNum(DocReportPlaceholderTmp));
    qbds.addRange(fieldNum(DocReportPlaceholderTmp, IsImage)).value(queryValue(NoYes::No));

    docSysTableLookup.addLookupfield(fieldNum(DocReportPlaceholderTmp, PlaceholderName), true);
    docSysTableLookup.addLookupfield(fieldNum(DocReportPlaceholderTmp, PlaceholderDesc));
    docSysTableLookup.addLookupfield(fieldNum(DocReportPlaceholderTmp, IsStandard));
    docSysTableLookup.addLookupfield(fieldNum(DocReportPlaceholderTmp, IsCustom));
    docSysTableLookup.addLookupfield(fieldNum(DocReportPlaceholderTmp, IsUserDefined));

    docSysTableLookup.parmQuery(query);
    docSysTableLookup.parmTmpBuffer(docReportPlaceholderTmp);

    docSysTableLookup.performFormLookup();

    // Set position to the selected placeholder in the lookup form 
    docSysTableLookup.datasource().formRun().args().lookupField(fieldNum(DocReportPlaceholderTmp, PlaceholderName));
    docSysTableLookup.datasource().formRun().args().lookupValue(_formControl.valueStr());
    docSysTableLookup.datasource().research(false);
}

/// <summary>
/// Refresh and validate the SharePoint settings.
/// </summary>
public void clicked()
{
    super();

    MultiSelectionHelper selectionHelper = MultiSelectionHelper::construct();
    selectionHelper.parmDataSource(DocReportSharePointSettings_ds);

    DocReportSharePointSettings spSettings = selectionHelper.getFirst();
    if (spSettings.RecId)
    {
        // Add marked records in the grid to the Set
        Set selectedRecordSet = new Set(Types::Record);
        while (spSettings)
        {
            selectedRecordSet.add(spSettings);
            spSettings = selectionHelper.getNext();
        }

        // Show progress bar and call validation method
        SysOperationSandbox::callStaticMethod(
                                    classNum(DocReportSharePointSettingsUtil),
                                    staticMethodStr(DocReportSharePointSettingsUtil, validateSharePointSettingsSandboxHelper),
                                    selectedRecordSet.pack(),
                                    "@DocentricAX4:SharePointSettingsValidateProcess",
                                    "@DocentricAX4:SharePointSettingsValidateProcessCompleted");

        // Refresh data sources
        DocReportSharePointSettings_ds.research(true);
        DocReportSharePointMetadata_ds.research(true);
    }
}

