/// <summary>
/// Present folder selection form to the user.
/// </summary>
public void clicked()
{
    super();

    str hostname = DocSharePointHelperV3::getSharePointHostNameFromUrl(DocReportSharePointSettings.SharePointSiteUrl);
    if (!hostname)
    {    
        hostname = DocSharePointHelperV3::getDefaultServer();
    }
    if (hostname)
    {
        DocSharePointFolderSelection spFolderSelection = DocSharePointFolderSelection::construct(
                                                                                            hostname, 
                                                                                            DocReportSharePointSettings.SharePointSiteUrl,
                                                                                            DocReportSharePointSettings.SharePointDocumentLibraryUrl);
        spFolderSelection.parmShowFolders(false);
        if (spFolderSelection && spFolderSelection.promptUser())
        {
            DocReportSharePointSettings.SharePointSiteUrl = spFolderSelection.parmSelectedSite().Url;
            DocReportSharePointSettings.SharePointDocumentLibraryUrl = spFolderSelection.getRelativeFolderPath();
        }
    }
    else
    {
        MenuFunction mf = new MenuFunction(menuItemDisplayStr(DocuParameters), MenuItemType::Display);
        DocGlobalHelper::handleError(strfmt("@ApplicationFoundation:DocuType_SharePointNotConfigured", mf.label()), false, funcName());
    }
}

/// <summary>
/// Navigate to the displayed address
/// </summary>
public void clicked()
{
    super();

    if (DocReportSharePointSettings.SharePointSiteUrl != '')
    {
        str address = DocFileMngHelper::addUrlDelimiter(DocReportSharePointSettings.SharePointSiteUrl) + DocReportSharePointSettings.SharePointDocumentLibraryUrl;

        if (strLen(address) > 0)
        {
            Browser browser = new Browser();
            browser.navigate(address, true, false);
        }
    }
}
