public class DocRestrictedPrinterByReport extends FormRun
{
    str callerFormName;
    QueryBuildDataSource qbdsFilter;
    boolean shouldPositionToCallerRestriction = false;
    DocReportId callerReportId;


}

private void checkCaller()
{
    callerFormName = '';
    if (element.args() && element.args().caller() && (element.args().caller() is FormRun))
    {
        FormRun callerForm = element.args().caller();
        callerFormName = callerForm.name();
    }

    if (element.args() && element.args().record())
    {
        if (element.args().record() is DocRestrictedPrinter)
        {
            // We came through jumpRef from the flat restriction form.
            // By default, this form would be filtered to the printer from the caller record,
            // but we don't want that.
            // Instead, we only want to be positioned to the corresponding report record.
            // We will do that during the first execution of the linkActive() method of the report datasource.
            DocReportTable_ds.query().dataSourceTable(tableNum(DocReportTable)).clearDynalinks();

            DocRestrictedPrinter callerRestriction = element.args().record() as DocRestrictedPrinter;
            callerReportId = callerRestriction.ReportId;
            shouldPositionToCallerRestriction = true;

            NavigationList.visible(true);
        }
        else if (element.args().record() is DocRestrictedPrintersView)
        {
            // We came through jumpRef from Restricted printers by printer form.
            // We want to be positioned on the corresponding report record.
            DocRestrictedPrintersView callerView = element.args().record() as DocRestrictedPrintersView;
            callerReportId = callerView.ReportId;
            shouldPositionToCallerRestriction = true;

            NavigationList.visible(true);
        }
    }

}

private str getFormCaption()
{
    str formCaption;

    if (callerFormName == formStr(DocReportTable))
    {
        // If we came from the report setup form, use the caller report name
        DocReportTable callerReport = element.args().record() as DocReportTable;
        formCaption = strFmt('%1 - %2 - %3', "@DocentricAX3:RestrictedPrintersByReport", callerReport.ReportId, curExt());
    }
    else
    {
        // This means we came from the parameters form. Set the basic caption.
        formCaption = strFmt('%1 - %2', "@DocentricAX3:RestrictedPrintersByReport", curExt());
    }

    return formCaption;
}

private void initForm()
{
    #SysSystemDefinedButtons

    // Hide the Attach button.
    FormCommandButtonControl button = this.control(this.controlId(#SystemDefinedAttachButton)) as FormCommandButtonControl;
    if (button)
    {
        button.visible(false);
    }

    // Hide the list of printers on the left side if we came from the report setup
    //LeftGridContainer.visible(callerFormName != formStr(DocReportTable));

    // Set the caption
    this.design().caption(this.getFormCaption());

    // Restriction info
    RestrictionInfo.colorScheme(FormColorScheme::RGB);
    if (DocPrinterManager::getRestrictPrintersMode() == DocRestrictPrinters::None)
    {
        RestrictionInfo.text("@DocentricAX3:RestrictionIsOff");
        RestrictionInfo.helpText("@DocentricAX3:RestrictionIsOffHelp");
        RestrictionInfo.backgroundColor(0xFFF4CE);   // yellow color from infolog warning
    }
    else
    {            
        RestrictionInfo.text("@DocentricAX3:RestrictionIsOn");
        RestrictionInfo.helpText("@DocentricAX3:RestrictionIsOnHelp");
        RestrictionInfo.backgroundColor(0xE5FFE5);   // light green color
    }

    // Make sure that all helper tables are correctly initialized.
    DocPrinterManager::initializeRestrictedPrintersTables();
}

