public display container exclusivityIndicator(DocRestrictedPrintersView _view)
{
    str symbol = '';
    LabelId label;

    if (this.getIsExclusive(_view))
    {
        symbol = ImageReferenceSymbol::Lock;
        label = "@DocentricAX4:RestrictedForOtherReports";
    }
    return ImageReference::constructForSymbol(symbol, label).pack();
}

private boolean getIsExclusive(DocRestrictedPrintersView _view)
{
    boolean isExclusive = false;

    // Return the information if the current combination Report-printer is exclusive.
    // It is true if:
    // 1. Current printer has the 'Restrict for new reports' and 'Restrict for unregistered reports' flags ON, and
    // 2. All other reports are restricted for this printer
    DocRestrictedPrinterInfo printerInfo = DocRestrictedPrinterInfo::findByPrinterName(_view.PrinterName);
    if (printerInfo.RestrictForNewReports && printerInfo.RestrictForUnregisteredReports)
    {
        // 1st condition met (this printer has both flags ON).
        // Check the 2nd condition.
        DocRestrictedPrintersView testView;

        select count(RecId) from testView
            where  testView.PrinterName == _view.PrinterName
                && testView.ReportId != _view.ReportId
                && testView.ReportId != DocConstant::RestrictedPrinterNewReports
                && testView.ReportId != DocConstant::RestrictedPrinterUnregisteredReports
                && testView.IsRestricted == NoYes::No
                && testView.DataAreaId == _view.DataAreaId;

        if (testView.RecId == 0)
        {
            isExclusive = true;
        }
    }

    return isExclusive;
}

