public void clicked()
{
    super();

    DocRestrictedPrinter restrictedPrinter;

    MultiSelectionHelper helper = MultiSelectionHelper::construct();
    helper.parmDatasource(AllowedPrinters_ds);

    DocRestrictedPrintersView allowed = helper.getFirst();

    try
    {
        RecordInsertList recordInsertList = new RecordInsertList(tableNum(DocRestrictedPrinter));

        while (allowed.RecId != 0)
        {
            if (allowed.PrinterName == DocConstant::RestrictedPrinterNewPrinters)
            {
                DocReportTable.RestrictForNewPrinters = NoYes::Yes;
                DocReportTable.update();
            }
            else
            {
                restrictedPrinter.initValue();
                restrictedPrinter.PrinterName = allowed.PrinterName;
                restrictedPrinter.ReportId = DocReportTable.ReportId;

                if (restrictedPrinter.validateWrite())
                {
                    recordInsertList.add(restrictedPrinter);
                }
            }

            allowed = helper.getNext();
        }

        recordInsertList.insertDatabase();

        // Refresh datasources
        AllowedPrinters_ds.research();
        RestrictedPrinters_ds.research();
    }
    catch
    {
        DocGlobalHelper::handleError(strFmt("@DocentricAX3:ErrorRestrictingPrintersForReport", DocReportTable.ReportId));
    }
}

public void clicked()
{
    super();

    DocRestrictedPrinter restrictedPrinter;

    int processedPrinters = 0;
    MultiSelectionHelper helper = MultiSelectionHelper::construct();
    helper.parmDatasource(RestrictedPrinters_ds);

    DocRestrictedPrintersView allowed = helper.getFirst();

    try
    {
        ttsbegin;

        while (allowed.RecId != 0)
        {
            processedPrinters++;

            if (allowed.PrinterName == DocConstant::RestrictedPrinterNewPrinters)
            {
                DocReportTable.RestrictForNewPrinters = NoYes::No;
                DocReportTable.update();
            }
            else
            {
                delete_from restrictedPrinter
                where restrictedPrinter.PrinterName == allowed.PrinterName
                    && restrictedPrinter.ReportId == DocReportTable.ReportId;
            }

            allowed = helper.getNext();
        }

        ttscommit;

        // Refresh datasources
        AllowedPrinters_ds.research();
        RestrictedPrinters_ds.research();
    }
    catch
    {
        DocGlobalHelper::handleError(strFmt("@DocentricAX3:ErrorAllowingPrintersForReport", DocReportTable.ReportId));
    }
}

