public boolean modified()
{
    boolean ret;

    ret = super();

    PrinterName.visible(FilterCombo.selection() == DocRestrictPrinterOrReportFilter::RestrictedFor
                    || FilterCombo.selection() == DocRestrictPrinterOrReportFilter::AllowedFor);

    // Apply the selected filter
    DocReportTable_ds.executeQuery();

    return ret;
}

public void lookup()
{
    SysTableLookup          sysTableLookup;
    Query                   query;
    QueryBuildDataSource    qbds;

    query = new Query();

    qbds = query.addDataSource(tableNum(SysCorpNetPrinters));
    qbds.addOrderByField(fieldNum(SysCorpNetPrinters, PrinterName));

    sysTableLookup = SysTableLookup::newParameters(tableNum(SysCorpNetPrinters), this);
    sysTableLookup.addLookupfield(fieldNum(SysCorpNetPrinters, PrinterName));
    sysTableLookup.addLookupfield(fieldNum(SysCorpNetPrinters, Description));
    sysTableLookup.addLookupfield(fieldNum(SysCorpNetPrinters, Active));

    sysTableLookup.addSelectionField(fieldNum(SysCorpNetPrinters, PrinterName));

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

public boolean validate()
{
    boolean ret;

    ret = super();

    str selectedPrinterName = PrinterName.text();
    SysCorpNetPrinters selectedPrinter;
    select firstonly RecId from selectedPrinter where selectedPrinter.PrinterName == selectedPrinterName;
    if (!selectedPrinter.RecId)
    {
        ret = ret && checkFailed(strFmt("@DocentricAX3:InvalidPrinterName", selectedPrinter));
    }

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    // Apply the selected filter
    DocReportTable_ds.executeQuery();

    return ret;
}

public void jumpRef()
{
    Args args = new Args();
    args.caller(element);

    SysCorpNetPrinters networkPrinters;
    select firstonly networkPrinters where networkPrinters.PrinterName == this.text();
    FormRun formRun = new MenuFunction(menuItemDisplayStr(SysCorpNetPrinters), MenuItemType::Display).create(args);
    formRun.run();
    formRun.dataSource(1).positionToRecord(networkPrinters);
}
