public void executeQuery()
{
    // If we are in a grid view,
    // and we selected some kind of filtering
    if (GridPanel.isActivePage() && (FilterCombo.selection() != DocRestrictPrinterOrReportFilter::All))
    {
        // The following combinations are valid:
        //  - with/without any restriction
        //  - with/without restriction for a given report, then ReportId must be provided
        if (FilterCombo.selection() == DocRestrictPrinterOrReportFilter::WithAnyRestriction
            || FilterCombo.selection() == DocRestrictPrinterOrReportFilter::WithoutAnyRestriction
            || ReportId.text() != '')
        {
            qbdsFilter.clearRanges();

            if (FilterCombo.selection() == DocRestrictPrinterOrReportFilter::RestrictedFor
                || FilterCombo.selection() == DocRestrictPrinterOrReportFilter::AllowedFor)
            {
                // We are looking specifically for restriction for given report.
                qbdsFilter.addRange(fieldNum(DocRestrictedPrinter, ReportId)).value(queryValue(ReportId.text()));
            }

            // Temporarily enable the helper Restricted printer datasource,
            // until super() executes the query, then we will disable it.
            qbdsFilter.enabled(true);

            switch (FilterCombo.selection())
            {
                case DocRestrictPrinterOrReportFilter::RestrictedFor:
                case DocRestrictPrinterOrReportFilter::WithAnyRestriction:
                    qbdsFilter.joinMode(JoinMode::ExistsJoin);
                    break;

                case DocRestrictPrinterOrReportFilter::AllowedFor:
                case DocRestrictPrinterOrReportFilter::WithoutAnyRestriction:
                    qbdsFilter.joinMode(JoinMode::NoExistsJoin);
                    break;

                default:
                // do nothing
            }
        }
    }

    // Execute the query (i.e. run super()) and then disable the helper Restricted printer
    // datasource until the next time we need it.
    super();

    // Get the number of records in the query resultset 
    // and add it to the form caption           
    element.design().caption(strFmt('%1 - %2 (printers count: %3)', 
                            "@DocentricAX3:RestrictedReportsByPrinter", curExt(),
                            SysQuery::countLoops(this.queryRun())));

    qbdsFilter.enabled(false);
}

public void linkActive()
{
    super();

    if (shouldPositionToCallerRestriction)
    {
        shouldPositionToCallerRestriction = false;

        DocRestrictedPrinterInfo localPrinterInfo = DocRestrictedPrinterInfo::findByPrinterName(callerPrinterName);
        this.positionToRecord(localPrinterInfo);
    }
}

public void init()
{
    super();

    QueryBuildDataSource qbds = this.query().dataSourceTable(tableNum(SysCorpNetPrinters));

    // On cloud environments make sure that we show only the printers which are enabled in DRA.
    DocPrinterManager::filterPrintersWithApp(qbds);

    // Link a helper Restricted printer datasource, but keep it disabled.
    // We will enable it only temporarily in executeQuery() for the root datasource (DocRestrictedPrinterInfo),
    // when we need to filter restricted or not restricted printers.
    qbdsFilter = qbds.addDataSource(tableNum(DocRestrictedPrinter));
    qbdsFilter.relations(true);
    qbdsFilter.enabled(false);
}

