private boolean getIsExclusive(DocRestrictedPrintersView _view)
{
    boolean isExclusive = false;

    // Return the information if the current combination Report-printer is exclusive.
    // It is true if:
    // 1. Current report has the 'Restrict for new printers' flag ON, and
    // 2. All other printers are restricted for this report
    if (DocReportTable::findReportId(_view.ReportId).RestrictForNewPrinters)
    {
        // 1st condition met (this report has the flag ON).
        // Check the 2nd condition.
        DocRestrictedPrintersView testView;
        select count(RecId) from testView
            where testView.PrinterName != _view.PrinterName
                && testView.PrinterName != DocConstant::RestrictedPrinterNewPrinters
                && testView.ReportId == _view.ReportId
                && testView.IsRestricted == NoYes::No
                && testView.DataAreaId == _view.DataAreaId;
        if (testView.RecId == 0)
        {
            isExclusive = true;
        }
    }

    return isExclusive;
}

display boolean isExclusive(DocRestrictedPrintersView _view)
{
    return this.getIsExclusive(_view);
}

public display container exclusivityIndicator(DocRestrictedPrintersView _view)
{
    str symbol = '';
    LabelId label;

    if (this.getIsExclusive(_view))
    {
        symbol = ImageReferenceSymbol::Lock;
        label = "@DocentricAX4:RestrictedForOtherPrinters";
    }

    return ImageReference::constructForSymbol(symbol, label).pack();
}

