public void clicked()
{
    super();

    DocRestrictedPrinter restrictedPrinter;

    MultiSelectionHelper helper = MultiSelectionHelper::construct();
    helper.parmDatasource(AllowedReports_ds);

    DocRestrictedPrintersView allowed = helper.getFirst();

    try
    {
        RecordInsertList recordInsertList = new RecordInsertList(tableNum(DocRestrictedPrinter));

        while (allowed.RecId != 0)
        {
            if (allowed.ReportId == DocConstant::RestrictedPrinterUnregisteredReports)
            {
                DocRestrictedPrinterInfo.RestrictForUnregisteredReports = NoYes::Yes;
                DocRestrictedPrinterInfo.update();
            }
            else if (allowed.ReportId == DocConstant::RestrictedPrinterNewReports)
            {
                DocRestrictedPrinterInfo.RestrictForNewReports = NoYes::Yes;
                DocRestrictedPrinterInfo.update();
            }
            else
            {
                restrictedPrinter.initValue();
                restrictedPrinter.PrinterName = SysCorpNetPrinters.PrinterName;
                restrictedPrinter.ReportId = allowed.ReportId;

                if (restrictedPrinter.validateWrite())
                {
                    recordInsertList.add(restrictedPrinter);
                }
            }

            allowed = helper.getNext();
        }

        recordInsertList.insertDatabase();

        // Refresh datasources
        DocRestrictedPrinterInfo_ds.reread();
        DocRestrictedPrinterInfo_ds.refresh();

        AllowedReports_ds.research();
        RestrictedReports_ds.research();
    }
    catch
    {
        DocGlobalHelper::handleError(strFmt("@DocentricAX3:ErrorRestrictingReportsForPrinter", SysCorpNetPrinters.PrinterName));
    }
}

public void clicked()
{
    super();

    DocRestrictedPrinter restrictedPrinter;

    MultiSelectionHelper helper = MultiSelectionHelper::construct();
    helper.parmDatasource(RestrictedReports_ds);

    DocRestrictedPrintersView allowed = helper.getFirst();

    try
    {
        ttsbegin;

        while (allowed.RecId != 0)
        {                    
            if (allowed.ReportId == DocConstant::RestrictedPrinterUnregisteredReports)
            {
                DocRestrictedPrinterInfo.RestrictForUnregisteredReports = NoYes::No;
                DocRestrictedPrinterInfo.update();
            }
            else if (allowed.ReportId == DocConstant::RestrictedPrinterNewReports)
            {
                DocRestrictedPrinterInfo.RestrictForNewReports = NoYes::No;
                DocRestrictedPrinterInfo.update();
            }
            else
            {
                delete_from restrictedPrinter
                    where restrictedPrinter.PrinterName == SysCorpNetPrinters.PrinterName
                        && restrictedPrinter.ReportId == allowed.ReportId;
            }

            allowed = helper.getNext();
        }

        ttscommit;

        // Refresh datasources
        DocRestrictedPrinterInfo_ds.reread();
        DocRestrictedPrinterInfo_ds.refresh();

        AllowedReports_ds.research();
        RestrictedReports_ds.research();
    }
    catch
    {
        DocGlobalHelper::handleError(strFmt("@DocentricAX3:ErrorAllowingReportsForPrinter", SysCorpNetPrinters.PrinterName));
    }
}

