public boolean modified()
{
    boolean ret;

    ret = super();

    ReportId.visible(FilterCombo.selection() == DocRestrictPrinterOrReportFilter::RestrictedFor
                    || FilterCombo.selection() == DocRestrictPrinterOrReportFilter::AllowedFor);

    // Apply the selected filter
    DocRestrictedPrinterInfo_ds.executeQuery();

    return ret;
}

public void lookup()
{
    SysTableLookup          sysTableLookup;
    Query                   query;
    QueryBuildDataSource    qbds;

    query = new Query();

    qbds = query.addDataSource(tableNum(DocReportTable));
    qbds.addOrderByField(fieldNum(DocReportTable, ReportId));

    sysTableLookup = SysTableLookup::newParameters(tableNum(DocReportTable), this);
    sysTableLookup.addLookupfield(fieldNum(DocReportTable, ReportId));

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

public boolean validate()
{
    boolean ret;

    ret = super();

    str selectedReportId = ReportId.text();
    if (!DocReportTable::findReportId(selectedReportId))
    {
        ret = ret && checkFailed(strFmt("@DocentricAX3:InvalidReportId", selectedReportId));
    }

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    // Apply the selected filter
    DocRestrictedPrinterInfo_ds.executeQuery();

    return ret;
}

public void jumpRef()
{
    Args args = new Args();
    args.caller(element);

    FormRun formRun = new MenuFunction(menuItemDisplayStr(DocReportTable), MenuItemType::Display).create(args);
    formRun.run();
    formRun.dataSource(1).positionToRecord(DocReportTable::findReportId(this.text()));
}
