[Form]
public class DocUserPrinterByPrinter extends FormRun
{
    QueryBuildDataSource qbdsFilterView, qbdsFilterUser;
    int totalNumOfUsers;
    DocUserPrinter callerUserPrinter;
    boolean shouldPositionToCallerUserPrinter;

}

private void initForm()
{
    #SysSystemDefinedButtons

    // Hide the Attach button.
    FormCommandButtonControl button = this.control(this.controlId(#SystemDefinedAttachButton)) as FormCommandButtonControl;
    if (button)
    {
        button.visible(false);
    }

    // Get caller-related info
    str callerFormName = '';
    if (element.args() && element.args().caller() && (element.args().caller() is FormRun))
    {
        FormRun callerForm = element.args().caller();
        callerFormName = callerForm.name();
    }
    if ((callerFormName == formStr(DocUserPrinterByUser) || callerFormName == formStr(DocUserPrinterFlat))
            && element.args().parm() && element.args().parm() == 'JumpRef'
            && element.args().record() && element.args().record() is DocUserPrinter)
    {
        callerUserPrinter = element.args().record() as DocUserPrinter;
        shouldPositionToCallerUserPrinter = true;

        NavigationList.visible(true);
    }

    // Set the captions
    this.design().caption(strFmt('%1 - %2', "@DocentricAX4:UserPrintersByPrinter", curExt()));
    UserPrintersTabPage.caption(DocPrinterManager::getUserPrintersMode() == DocUserPrinterMode::UserFavoritePrinters ?
                                    "@DocentricAX4:UsersWithThisPrinterAsFavorite" : "@DocentricAX4:UsersAllowedForPrinter");

    SysCorpNetPrinters_ds.query().dataSourceTable(tableNum(SysCorpNetPrinters)).clearDynalinks();

    // Get total number of users, we will need it for some filtering.
    // If someone adds a new user while we are on this form, we won't see it
    // until the form is re-initialized, but I think that is OK.
    totalNumOfUsers = this.getTotalUsers();
}

public void setControls()
{
    // Show/hide user filter combo.
    UserIdForFilter.visible(FilterCombo.selection() == DocUserPrinterPrinterFilter::WithUser
                        || FilterCombo.selection() == DocUserPrinterPrinterFilter::WithoutUser);

    // Show/hide top menu items
    ActionsButtonGroup.visible(GridPanel.isActivePage());
}

private int getTotalUsers()
{       
    int cnt = 0; 
    UserInfo userInfoLocal;
    Set userIds = new Set(Types::String);

    // select count() + group by doesn't work, so we have to loop.
    // Some IDs are repeated in the database, even for the same company, 
    // and User info form ommits the duplicates, so we should do the same.
    while select id from userInfoLocal
        group by userInfoLocal.id
        where userInfoLocal.enable == NoYes::Yes
            && userInfoLocal.accountType != UserAccountType::ClaimsGroup
            && userInfoLocal.isMicrosoftAccount == NoYes::No
    {
        if (!userIds.in(userInfoLocal.id))
        {
            cnt++;
            userIds.add(userInfoLocal.id);
        }
    }

    return cnt;
}

