public void executeQuery()
{
    QueryBuildDataSource qbds = this.query().dataSourceTable(tableNum(SysCorpNetPrinters));

    // If we are in a grid view,
    // and we selected some kind of filtering
    if (GridPanel.isActivePage() && (FilterCombo.selection() != DocUserPrinterPrinterFilter::All))
    {
        // The following combinations are valid:
        //  - with/without any restriction
        //  - with/without restriction for a given user, then UserId must be provided
        if (FilterCombo.selection() == DocUserPrinterPrinterFilter::WithAllUsers
            || FilterCombo.selection() == DocUserPrinterPrinterFilter::WithAnyUser
            || FilterCombo.selection() == DocUserPrinterPrinterFilter::WithoutAnyUser
            || UserIdForFilter.text() != '')
        {
            qbdsFilterUser.clearRanges();
            qbdsFilterView.clearRange(fieldNum(DocUserPrinterByPrinterView, PrinterName));
            qbdsFilterView.clearRange(fieldNum(DocUserPrinterByPrinterView, UserPrinterCount));

            if (FilterCombo.selection() == DocUserPrinterPrinterFilter::WithUser
                || FilterCombo.selection() == DocUserPrinterPrinterFilter::WithoutUser)
            {
                // We are looking specifically for restriction for given user.
                qbdsFilterUser.addRange(fieldNum(DocUserPrinter, UserId)).value(queryValue(UserIdForFilter.text()));
            }
            else if (FilterCombo.selection() == DocUserPrinterPrinterFilter::WithAllUsers)
            {
                // Compare DocUserPrinterByPrinterView.UserPrinterCount with total number of users
                qbdsFilterView.addRange(fieldNum(DocUserPrinterByPrinterView, RecId)).value(
                                        strFmt('((%1.PrinterName == Helper.PrinterName) && (Helper.UserPrinterCount == %2))',
                                                qbds.name(), totalNumOfUsers));
            }

            switch (FilterCombo.selection())
            {
                case DocUserPrinterPrinterFilter::WithAnyUser:
                case DocUserPrinterPrinterFilter::WithUser:
                    qbdsFilterUser.enabled(true);
                    qbdsFilterUser.joinMode(JoinMode::ExistsJoin);
                    break;

                case DocUserPrinterPrinterFilter::WithoutAnyUser:
                case DocUserPrinterPrinterFilter::WithoutUser:
                    qbdsFilterUser.enabled(true);
                    qbdsFilterUser.joinMode(JoinMode::NoExistsJoin);
                    break;

                case DocUserPrinterPrinterFilter::WithAllUsers:
                    // We made the needed join in the init() of this datasource,
                    // and it is always the same for this kind of a filter,
                    // so only enable this helper datasource
                    qbdsFilterView.enabled(true);
                    break;

                default:
                // do nothing
            }
        }
    }

    // Execute the query (i.e. run super()) and then disable the helper datasources until the next time we need them.
    super();

    qbdsFilterUser.enabled(false);
    qbdsFilterView.enabled(false);
}

public void init()
{
    super();

    QueryBuildDataSource qbds = this.query().dataSourceTable(tableNum(SysCorpNetPrinters));

    // On cloud environments make sure that we show only the printers which are enabled in DRA.
    DocPrinterManager::filterPrintersWithApp(qbds);

    // Link a helper DocUserPrinterByPrinterView datasource, but keep it disabled.
    // We will enable it only temporarily in executeQuery() for the root datasource (SysCorpNetPrinters),
    // when we need to filter the printers based on the assigned users.
    qbdsFilterView = qbds.addDataSource(tableNum(DocUserPrinterByPrinterView));
    qbdsFilterView.relations(true);
    qbdsFilterView.name('Helper');
    qbdsFilterView.enabled(false);
    qbdsFilterView.relations(false);
    qbdsFilterView.addLink(fieldNum(SysCorpNetPrinters, PrinterName), fieldNum(DocUserPrinterByPrinterView, PrinterName));
    qbdsFilterView.addLink(fieldNum(SysCorpNetPrinters, DataAreaId), fieldNum(DocUserPrinterByPrinterView, DataAreaId));
    qbdsFilterView.joinMode(JoinMode::InnerJoin);
    //qbdsFilterView.addRange(fieldNum(DocUserPrinterView, DataArea)).value(queryValue(curExt()));
    qbdsFilterView.update(false);

    // Link a helper DocUserPrinter datasource, but keep it disabled.
    // We will enable it only temporarily in executeQuery() for the root datasource (SysCorpNetPrinters),
    // when we need to filter based on the assigned users.
    qbdsFilterUser = qbds.addDataSource(tableNum(DocUserPrinter));
    qbdsFilterUser.enabled(false);
    qbdsFilterUser.relations(true);
    qbdsFilterView.update(false);
}

public void jumpRef()
{
    // When printer is clicked, open the Network printers form
    // and get positioned to the exactly same record as this one.
    Args args = new Args();
    args.caller(element);

    SysCorpNetPrinters targetPrinter;
    select firstonly targetPrinter where targetPrinter.PrinterName == SysCorpNetPrinters.PrinterName;
    args.lookupRecord(targetPrinter);

    new MenuFunction(menuItemDisplayStr(SysCorpNetPrinters), MenuItemType::Display).run(args);
}

public void init()
{
    super();

    QueryBuildDataSource qbds = this.query().dataSourceName('DocUserPrinterByPrinterView');

    qbds.relations(false);
    qbds.addLink(fieldNum(SysCorpNetPrinters, PrinterName), fieldNum(DocUserPrinterByPrinterView, PrinterName));
    qbds.addLink(fieldNum(SysCorpNetPrinters, DataAreaId), fieldNum(DocUserPrinterByPrinterView, DataAreaId));
}

