    return printerInfo;
}

public void delete()
{
    // Deleting the record directly from UI is possible only in detail view,
    // when we are on a known printer record.
    super();

    // Force updating the display method with number of users per printer
    SysCorpNetPrinters currentPrinter;
    select currentPrinter where currentPrinter.PrinterName == SysCorpNetPrinters.PrinterName;

    SysCorpNetPrinters_ds.research();
    SysCorpNetPrinters_ds.positionToRecord(currentPrinter);
}

public void linkActive()
{
    super();

    if (shouldPositionToCallerUserPrinter)
    {
        shouldPositionToCallerUserPrinter = false;

        SysCorpNetPrinters callerPrinter;
        select firstonly callerPrinter where callerPrinter.PrinterName == callerUserPrinter.PrinterName;
        if (callerPrinter)
        {
            SysCorpNetPrinters_ds.positionToRecord(callerPrinter);
        }
    }
}

public void modified()
{
    super();

    DocUserPrinter_ds.executeQuery();
}

public void jumpRef()
{
    // When user is clicked, open the User printer by user form
    // and get positioned to the exactly same record as this one.
    Args args = new Args();
    args.caller(element);
    args.record(DocUserPrinter);
    args.parm('JumpRef');
    args.formViewOption(FormViewOption::Details);

    new MenuFunction(menuItemDisplayStr(DocUserPrinterByUser), MenuItemType::Display).run(args);
}

[SysClientCacheDataMethodAttribute]
display str displayPerson(UserInfo _userInfo)
{
    str personName;

    PartyIProvider partyProvider = PartyProviderFactory::getPartyProvider();

    if (partyProvider)
    {
        personName = partyProvider.dirPersonUserId2Name(_userInfo.id);
    }

    return personName;
}

