[Form]
public class DocUserPrinterByUser extends FormRun
{
    QueryBuildDataSource qbdsFilterView, qbdsFilterPrinter, qbdsFilterRole;
    int totalNumOfPrinters;    
    PartyIProvider partyProvider;
    DocUserPrinter callerUserPrinter;
    boolean shouldPositionToCallerUserPrinter;

}

private void initForm()
{
    #SysSystemDefinedButtons

    // Hide the Attach button.
    FormCommandButtonControl button = this.control(this.controlId(#SystemDefinedAttachButton)) as FormCommandButtonControl;
    if (button)
    {
        button.visible(false);
    }

    // Get caller-related info
    str callerFormName = '';
    if (element.args() && element.args().caller() && (element.args().caller() is FormRun))
    {
        FormRun callerForm = element.args().caller();
        callerFormName = callerForm.name();
    }
    if ((callerFormName == formStr(DocUserPrinterByPrinter) || callerFormName == formStr(DocUserPrinterFlat))
            && element.args().parm() && element.args().parm() == 'JumpRef'
            && element.args().record() && element.args().record() is DocUserPrinter)
    {
        callerUserPrinter = element.args().record() as DocUserPrinter;
        shouldPositionToCallerUserPrinter = true;
    }

    // Set the captions
    this.design().caption(strFmt('%1 - %2', "@DocentricAX4:UserPrintersByUser", curExt()));
    UserPrintersTabPage.caption(DocPrinterManager::getUserPrintersMode() == DocUserPrinterMode::UserFavoritePrinters ?
                                                "@DocentricAX4:UserFavoritePrinters" : "@DocentricAX4:UserAllowedPrinters");

    UserInfo_ds.query().dataSourceTable(tableNum(UserInfo)).clearDynalinks();

    // Hide the list of users on the left side and user info on the top if we came from the User options form
    NavigationList.visible(callerFormName != formStr(SysUserSetup));
    UserInfoTabPage.visible(callerFormName != formStr(SysUserSetup));

    // Get total number of printers, we will need it for some filtering.
    // If someone adds a new printer while we are on this form, we won't see it
    // until the form is re-initialized, but I think that is OK.
    totalNumOfPrinters = this.getTotalPrinters();

    // We use this in display methods
    partyProvider = PartyProviderFactory::getPartyProvider(false);

    // We will be changing the color of Effective default printer field                                
    DefaultPrinterEffective.colorScheme(FormColorScheme::RGB);        

    // Enable/disable the buttons Up, Down, Set default.
    // They are always enabled for admins. 
    // For other users, these buttons are enabled if we came from the User options form
    // and:
    //      - For Up/Down buttons: Printers mode is Favorite printers.
    //      - For Set default: 'Allow users to set up default printer' is ON. 
    DocDataSecurityManager dataSecMng = new DocDataSecurityManager();
    boolean isAdmin = dataSecMng.isInRoleSystemAdministrator() || dataSecMng.isInRoleDocentricAdministrator();
    boolean isUserSetupForFavoritePrinters = callerFormName == formStr(SysUserSetup) && DocPrinterManager::getUserPrintersMode() == DocUserPrinterMode::UserFavoritePrinters;
    boolean shouldEnableSetDefault = callerFormName == formStr(SysUserSetup) && DocParameters::find().AllowUserSetDefaultPrinter;
    MoveUp.enabled(isAdmin || isUserSetupForFavoritePrinters);
    MoveDown.enabled(isAdmin || isUserSetupForFavoritePrinters);
    SetDefaultPrinter.enabled(isAdmin || shouldEnableSetDefault);

    // Enable/disable menu items Add printers, Add all printers in detail view, bottom grid.
    // They are always enabled for admins.
    // For other users, they are enabled if we came from the User options form
    // and Printers mode is Favorite printers.
    AddSelectedPrintersBottom.enabled(isAdmin || isUserSetupForFavoritePrinters);
    AddAllPrintersBottom.enabled(isAdmin || isUserSetupForFavoritePrinters);
}

public void setControls()
{
    // Show/hide pinter combo and security role combo.
    PrinterName.visible(FilterByPrinterCombo.selection() == DocUserPrinterUserFilter::WithPrinter
                        || FilterByPrinterCombo.selection() == DocUserPrinterUserFilter::WithoutPrinter);        
    SecurityRoleName.visible(FilterByRoleCombo.selection() != DocUserPrinterRoleFilter::All);

    // Show/hide top menu items
    ActionsButtonGroup.visible(GridPanel.isActivePage());
}

private int getTotalPrinters()
{
    SysCorpNetPrinters networkPrinters;
    select count(RecId) from networkPrinters;

    return int642int(networkPrinters.RecId);
}

public void init()
{
    super();

    this.initForm();

    element.setControls();
}

