public void executeQuery()
{
    QueryBuildDataSource qbds = this.query().dataSourceTable(tableNum(UserInfo));
    //qbds.clearRange(fieldNum(UserInfo, enable));
    //qbds.addRange(fieldNum(UserInfo, enable)).value(queryValue(true));

    // If we are in a grid view,
    // and we selected some kind of filtering by printer
    if (GridPanel.isActivePage() && (FilterByPrinterCombo.selection() != DocUserPrinterUserFilter::All))
    {
        // The following combinations are valid:
        //  - with/without any restriction
        //  - with/without restriction for a given printer, then PrinterName must be provided
        if (FilterByPrinterCombo.selection() == DocUserPrinterUserFilter::WithAllPrinters
            || FilterByPrinterCombo.selection() == DocUserPrinterUserFilter::WithAnyPrinter
            || FilterByPrinterCombo.selection() == DocUserPrinterUserFilter::WithoutAnyPrinter
            || PrinterName.text() != '')
        {
            qbdsFilterPrinter.clearRanges();
            qbdsFilterView.clearRange(fieldNum(DocUserPrinterView, UserId));
            qbdsFilterView.clearRange(fieldNum(DocUserPrinterView, UserPrinterCount));

            if (FilterByPrinterCombo.selection() == DocUserPrinterUserFilter::WithPrinter
                || FilterByPrinterCombo.selection() == DocUserPrinterUserFilter::WithoutPrinter)
            {
                // We are looking specifically for restriction for given printer.
                qbdsFilterPrinter.addRange(fieldNum(DocUserPrinter, PrinterName)).value(queryValue(PrinterName.text()));
            }
            else if (FilterByPrinterCombo.selection() == DocUserPrinterUserFilter::WithAllPrinters)
            {
                // Compare DocUserPrinterView.UserPrinterCount with tottal number of printers
                qbdsFilterView.addRange(fieldNum(DocUserPrinterView, RecId)).value(
                                        strFmt('((%1.id == Helper.UserId) && (Helper.UserPrinterCount == %2))',
                                                qbds.name(), totalNumOfPrinters));
            }                    

            switch (FilterByPrinterCombo.selection())
            {
                case DocUserPrinterUserFilter::WithAnyPrinter:
                case DocUserPrinterUserFilter::WithPrinter:
                    qbdsFilterPrinter.enabled(true);
                    qbdsFilterPrinter.joinMode(JoinMode::ExistsJoin);
                    break;

                case DocUserPrinterUserFilter::WithoutAnyPrinter:
                case DocUserPrinterUserFilter::WithoutPrinter:
                    qbdsFilterPrinter.enabled(true);
                    qbdsFilterPrinter.joinMode(JoinMode::NoExistsJoin);
                    break;

                case DocUserPrinterUserFilter::WithAllPrinters:
                    // We made the needed join in the init() of this datasource,
                    // and it is always the same for this kind of a filter, 
                    // so only enable this helper datasource
                    qbdsFilterView.enabled(true);
                    break;

                default:
                // do nothing
            }
        }
    }

    // If we are in a grid view,
    // and we selected filtering by user role
    if (GridPanel.isActivePage() && FilterByRoleCombo.selection() != DocUserPrinterRoleFilter::All)
    {
        QueryBuildDataSource qbdsSecurityRole = qbdsFilterRole.childDataSourceNo(1);
        qbdsSecurityRole.clearRanges();

        // We are looking for a specified role
        qbdsSecurityRole.addRange(fieldNum(SecurityRole, Name)).value(queryValue(SecurityRoleName.text()));

        // Temporarily enable the helper role datasource,
        // until super() executes the query, then we will disable it.
        qbdsFilterRole.enabled(true);

        switch (FilterByRoleCombo.selection())
        {
            case DocUserPrinterRoleFilter::WithUserRole:
                qbdsFilterRole.joinMode(JoinMode::ExistsJoin);
                break;

            case DocUserPrinterRoleFilter::WithoutUserRole:
                qbdsFilterRole.joinMode(JoinMode::NoExistsJoin);
                break;

            default:
            // do nothing
        }

    }

    // Execute the query (i.e. run super()) and then disable the helper datasources until the next time we need them.
    super();  

    qbdsFilterPrinter.enabled(false);
    qbdsFilterView.enabled(false);
    qbdsFilterRole.enabled(false);
}

public void init()
{
    super();

    QueryBuildDataSource qbds = this.query().dataSourceTable(tableNum(UserInfo));

    qbds.addRange(fieldNum(UserInfo, enable)).value(queryValue(true));
    qbds.addRange(fieldNum(UserInfo, accountType)).value(sysquery::valueNot(enum2str(UserAccountType::ClaimsGroup)));   // Filter out ClaimsGroup, User info form does the same
    qbds.addRange(fieldNum(UserInfo, isMicrosoftAccount)).value(int2Str(false));    // Ensure system accounts are filtered out, User info form does the same

    // Filter by user if we came from user setup form
    if (element.args() && element.args().callerName() == formStr(SysUserSetup))
    {
        QueryBuildRange qbr = qbds.addRange(fieldNum(UserInfo, id));
        qbr.status(RangeStatus::Locked);
        if (element.args().parm())
        {
            // We came from UserInfo page --> open settings for the selected user
            qbr.value(queryValue(element.args().parm()));
        }
        else
        {
            // We came directly to user settings (from Settings button on menu) --> open settings
            // for the current user
            qbr.value(queryValue(curUserId()));
        }
    }                 

    // Link a helper DocUserPrinterView datasource, but keep it disabled.
    // We will enable it only temporarily in executeQuery() for the root datasource (UserInfo),
    // when we need to filter based on the assigned printers.
    // We will apply qbdsFilterView or qbdsFilterPrinter, depending on the filter condition.
    qbdsFilterView = qbds.addDataSource(tableNum(DocUserPrinterView));
    qbdsFilterView.name('Helper');
    qbdsFilterView.enabled(false);
    qbdsFilterView.relations(false);
    qbdsFilterView.addLink(fieldNum(UserInfo, id), fieldNum(DocUserPrinterView, UserId));
    qbdsFilterView.joinMode(JoinMode::InnerJoin);
    qbdsFilterView.addRange(fieldNum(DocUserPrinterView, DataArea)).value(queryValue(curExt()));
    qbdsFilterView.update(false);

    // Link a helper DocUserPrinter datasource, but keep it disabled.
    // We will enable it only temporarily in executeQuery() for the root datasource (UserInfo),
    // when we need to filter based on the assigned printers.
    qbdsFilterPrinter = qbds.addDataSource(tableNum(DocUserPrinter));
    qbdsFilterPrinter.enabled(false);
    qbdsFilterPrinter.relations(true);
    qbdsFilterPrinter.update(false);

    // Link a helper SecurityUserRole datasource (further joined to SecurityRole).
    // We will enable it only temporarily in executeQuery() for the root datasource (UserInfo),
    // when we need to filter based on the user role.
    qbdsFilterRole = qbds.addDataSource(tableNum(SecurityUserRole));
    qbdsFilterRole.enabled(false);
    qbdsFilterRole.relations(true);
    qbdsFilterRole.update(false);

    QueryBuildDataSource qbdsTmp = qbdsFilterRole.addDataSource(tableNum(SecurityRole));
    qbdsTmp.relations(true);
    qbdsTmp.joinMode(JoinMode::ExistsJoin);
    qbdsTmp.update(false);
}

