public int active()
{
    int ret;

    ret = super();

    PrinterName defaultPrinter = DocDefaultPrintersView.DefaultPrinterEffective;

    DocUserPrinter localUserPrinter;
    select firstonly RecId from localUserPrinter
        where localUserPrinter.PrinterName == defaultPrinter
            && localUserPrinter.UserId == UserInfo.id;

    // If effective default printer for the selected user isn't among his user printers,
    // set the color of Effective default printer field to red (if in Allowed mode)
    // or yellow (if in Favorite mode and user selected mode is Favorite printers only).            
    DefaultPrinterEffective.backgroundColor(0xFFFFFF);
    if (!localUserPrinter.RecId)
    {
        // Effective default printer not found among the user printers.
        if (DocPrinterManager::getUserPrintersMode() == DocUserPrinterMode::UserFavoritePrinters)
        {                    
            DocFavoritePrinterSelection favoritePrinterSelection = SysUserInfo::find(UserInfo.id).FavoritePrinterSelection_DC;
            if (favoritePrinterSelection == DocFavoritePrinterSelection::FavoriteOnly)
            {
                // Printers mode is User favorite printers, and user configured
                // to see only his favorite printers. He won't get his default printer
                // offered in the printer lookup.
                DefaultPrinterEffective.backgroundColor(0xFFF4CE);  // yellow color from infolog warning
            }
        }
        else
        {
            // Printers mode is User allowed printers. User won't see his default printer
            // in the printer lookup and will get runtime error when printing to @DEFAULT_PRINTE@.
            DefaultPrinterEffective.backgroundColor(0xFDE7E9);  // red color from infolog error
        }
    }

    return ret;
}

