[SysClientCacheDataMethodAttribute]
display str displayUserInformation(DocUserPrinterView _view)
{
    str personName;

    if (partyProvider)
    {
        personName = partyProvider.dirPersonUserId2Name(_view.UserId);
    }

    str userInformation = personName == '' ?                                     
                            strFmt('Printers: %1', _view.UserPrinterCount) :
                            strFmt('%1, Printers: %2', personName, _view.UserPrinterCount);

    return userInformation;
}

