public void delete()
{
    // Deleting the record directly from UI is possible only in detail view,
    // when we are on a known user record.
    super();

    // Force updating the display method with number of users per printer
    UserInfo currentUser;
    select currentUser where currentUser.id == UserInfo.id;

    UserInfo_ds.research();
    UserInfo_ds.positionToRecord(currentUser);
}

public void linkActive()
{
    super();

    if (shouldPositionToCallerUserPrinter)
    {
        shouldPositionToCallerUserPrinter = false;

        UserInfo callerUserInfo;
        select firstonly callerUserInfo where callerUserInfo.id == callerUserPrinter.UserId;
        if (callerUserInfo)
        {
            UserInfo_ds.positionToRecord(callerUserInfo);
        }
    }
}

