public void clicked()
{ 
    DocUserPrinter currentRecord = DocUserPrinter.data();
    PrinterName selectedPrinterName = currentRecord.PrinterName;

    DocUserPrinterSettings defaultPrinter;
    update_recordset defaultPrinter setting DefaultPrinterName = selectedPrinterName
        where defaultPrinter.UserId == UserInfo.id;

    // Force refreshing the Effective default printer value
    DocDefaultPrintersView_ds.reread();
    DocDefaultPrintersView_ds.refresh();

    // Change the color of Effective default printer if needed
    UserInfo_ds.active();

    // Force refreshing the 'Is default' field
    DocUserPrinter_ds.research();
    DocUserPrinter_ds.positionToRecord(currentRecord);

    super();
}

public void pageActivated()
{
    element.setControls();

    UserInfo_ds.research(true);

    super();
}

