public boolean validate()
{
    boolean ret;

    ret = super();

    str selectedPrinterName = PrinterName.text();            
    return ret && DocPrinterManager::isPrinterActiveAndAllowedForUser('', selectedPrinterName, selectedPrinterName, true);
}

